import { Component, Input, Output, EventEmitter } from '@angular/core';
import { CardComponent } from "../card/card.component";
import { CommonModule } from '@angular/common';

@Component({
  selector: 'app-card-list',
  standalone: true,
  templateUrl: 'card-list.component.html',
  styleUrls: ['card-list.component.scss'],
  imports: [CardComponent,CommonModule],
})
export class CardListComponent {
  @Input() cards: any[] = [];
}

import { CommonModule } from '@angular/common';
import { Component } from '@angular/core';
import { MatTabsModule, MatTabChangeEvent } from '@angular/material/tabs';
import { MonthlyExposureChartComponent } from "../monthly-exposure-chart/monthly-exposure-chart.component";
import { ExposureRateChartComponent } from "../exposure-rate-chart/exposure-rate-chart.component";
@Component({
  selector: 'app-charts-tabs',
  imports: [MatTabsModule, CommonModule, MonthlyExposureChartComponent, ExposureRateChartComponent],
  templateUrl: './charts-tabs.component.html',
  styleUrl: './charts-tabs.component.scss'
})
export class ChartsTabsComponent {
  loadMonthChart: boolean = true;
  loadExposureChart: boolean = false;
  activeIndex: number = 0;
  onTabChange(event: MatTabChangeEvent) {
    const index = event.index;
    this.activeIndex = index;
    switch (index) {
      case 0:
        this.loadMonthChart = true;
        this.loadExposureChart = false;
        break;
      case 1:
        this.loadExposureChart = true;
        this.loadMonthChart = false;
        break;
      default:
        break;
    }
  }
}

import { Component } from '@angular/core';
import { AnnualExposureDetailsComponent } from "./components/annual-exposure-details/annual-exposure-details.component";
import { BreadcrumbWalletComponent } from './components/breadcrumb-wallet/breadcrumb-wallet.component';
import { ChartsTabsComponent } from "./components/charts-tabs/charts-tabs.component";
import { RiskEstimateChartComponent } from "./components/risk-estimate-chart/risk-estimate-chart.component";
import { TradeViewChartComponent } from "./components/trade-view-chart/trade-view-chart.component";
import { VolatilityChartComponent } from "./components/volatility-chart/volatility-chart.component";
import { MomentumIndicatorChartComponent } from "./components/momentum-indicator-chart/momentum-indicator-chart.component";

@Component({
  selector: 'app-dashboard',
  imports: [AnnualExposureDetailsComponent, BreadcrumbWalletComponent, ChartsTabsComponent, RiskEstimateChartComponent, TradeViewChartComponent, VolatilityChartComponent, MomentumIndicatorChartComponent],
  templateUrl: './dashboard.component.html',
  styleUrl: './dashboard.component.scss'
})
export class DashboardComponent {

}

import { CommonModule } from '@angular/common';
import { Component, Input } from '@angular/core';
import { FormsModule } from '@angular/forms';
import { MatOptionModule } from '@angular/material/core';
import { MatFormFieldModule } from '@angular/material/form-field';
import { MatSelectModule } from '@angular/material/select';

@Component({
  selector: 'app-dropdown',
  standalone: true,
  imports: [MatSelectModule, MatFormFieldModule, MatOptionModule,CommonModule,FormsModule],
  templateUrl:'./dropdown.component.html',
  styleUrls: ['./dropdown.component.scss']
})
export class DropdownComponent {
  @Input() options: { flag: string; label: string; description: string; value: string }[] = [];
  @Input() selectedValue: string = '';
  @Input() disabled: boolean = false;
  searchQuery: string = '';
  getSelectedOption() {
    return this.options.find(option => option.value === this.selectedValue);
  }
  get filteredOptions() {
    return this.options.filter(option =>
      option.label.toLowerCase().includes(this.searchQuery.toLowerCase()) ||
      option.description.toLowerCase().includes(this.searchQuery.toLowerCase())
    );
  }
  clearSearch(){
    this.searchQuery = "";
  }
}

