::ng-deep {
    .currency-dropdown-container {
        width: 100%;
        max-width: 515px;
    }

    .currency-dropdown {
        display: flex;
        font-family: "Heebo", sans-serif !important;
        border-radius: 6px;
        border: 1px solid #E5E7EB;

        &[aria-expanded="true"] {
            border-color: #2563EB;

            .mat-mdc-select-placeholder {
                color: #2947F2;
            }

            .mat-mdc-select-arrow-wrapper {
                .mat-mdc-select-arrow {
                    background-image: url('../../../../assets/up-arrow.svg');
                    background-repeat: no-repeat;
                    background-size: cover;
                }
            }
        }

        &.mat-mdc-select-disabled {
            pointer-events: none;
            opacity: 0.5;
            filter: grayscale(1);
            background-color: #F9FAFB;
        }

        &:hover {
            border-color: #2563EB;
        }

        .mat-mdc-select-trigger {
            padding: 13px 15px;
        }

        .mat-mdc-select-placeholder {
            color: #9CA3AF;
            font-size: 16px;
            font-weight: 400;
            line-height: 32px;
        }

        mat-select-trigger {
            .to-currency-section {
                display: flex;
                align-items: center;
                gap: 9px;

                .image-wrap {
                    position: relative;

                    .currency-image {
                        display: inline-block;
                        width: 28px;
                        height: 28px;
                        border-radius: 50%;
                    }

                    .tick-icn {
                        background-color: #fff;
                        border-radius: 50%;
                        position: absolute;
                        bottom: 0;
                        right: 0;
                    }
                }

                .currency-info {
                    display: flex;
                    flex-direction: column;

                    .currency-code {
                        color: #2947F2;
                        font-size: 18px;
                        font-style: normal;
                        font-weight: 700;
                        line-height: 23px;
                        margin: 0;
                    }

                    .currency-name {
                        color: #01031C;
                        font-size: 12px;
                        font-style: normal;
                        font-weight: 700;
                        line-height: 1;
                    }
                }
            }
        }

        .mat-mdc-select-arrow-wrapper {
            .mat-mdc-select-arrow {
                background-image: url('../../../../assets/down-arrow.svg');
                width: 24px;
                height: 24px;
                background-repeat: no-repeat;
                background-size: cover;

                svg {
                    display: none;
                }
            }
        }

        @media screen and (max-width:1024px) {
            .mat-mdc-select-trigger {
                padding: 12px;
            }

            .mat-mdc-select-placeholder {
                font-size: 14px;
                line-height: 24px;
            }

            .mat-mdc-select-arrow-wrapper {
                .mat-mdc-select-arrow {
                    width: 20px;
                    height: 20px;
                }
            }

            mat-select-trigger {
                .to-currency-section {
                    gap: 5px;

                    .image-wrap {
                        .currency-image {
                            width: 20px;
                            height: 20px;
                        }

                        .tick-icn {
                            width: 12px;
                            height: 12px;
                        }
                    }

                    .currency-info {
                        .currency-code {
                            font-size: 16px;
                            line-height: 20px;
                        }

                        .currency-name {
                            font-size: 10px;
                        }
                    }
                }
            }
        }

        @media screen and (max-width:767px) {
            .mat-mdc-select-trigger {
                padding: 10px;
            }

            .mat-mdc-select-placeholder {
                font-size: 12px;
                line-height: 20px;
            }

            .mat-mdc-select-arrow-wrapper {
                .mat-mdc-select-arrow {
                    width: 16px;
                    height: 16px;
                }
            }

            mat-select-trigger {
                .to-currency-section {
                    gap: 5px;

                    .image-wrap {
                        .currency-image {
                            width: 16px;
                            height: 16px;
                        }

                        .tick-icn {
                            width: 10px;
                            height: 10px;
                        }
                    }

                    .currency-info {
                        .currency-code {
                            font-size: 14px;
                            line-height: 16px;
                        }

                        .currency-name {
                            font-size: 8px;
                        }
                    }
                }
            }
        }
    }

    .mat-mdc-select-panel {
        &.currency-dropdown-panel {
            margin: 5px 0 0;
            padding: 36px 22px 36px 36px;
            border-radius: 6px;
            box-shadow: 0px 25px 50px -12px rgba(0, 0, 0, 0.25);

            .search-input-wrapper {
                display: flex;
                flex-direction: column;

                .input-inner {
                    display: flex;

                    input {
                        width: 100%;
                        border-radius: 7px;
                        border: 1px solid #2947F2;
                        background: #FFF;
                        box-shadow: 0px 6px 44px 0px rgba(65, 90, 235, 0.11);
                        display: flex;
                        height: 44px;
                        padding: 0px 10px 0px 20px;
                        align-items: center;
                        gap: 10px;
                        color: #5B5B66;
                        font-size: 14px;
                        font-weight: 400;

                        &::placeholder {
                            color: #5B5B66;
                        }

                        &:focus-visible {
                            outline: none;
                        }
