                }

                .error-wrap {
                    margin: 14px 0 0;
                    padding: 9px 29px;

                    .error-title {
                        color: #01031C;
                        font-size: 18px;
                        font-weight: 400;
                        line-height: 35px;
                    }

                    .desc {
                        color: #585858;
                        font-size: 14px;
                        font-weight: 400;
                        line-height: normal;

                        .clear-search {
                            background-color: transparent;
                            color: #175CD3;
                            font-size: 14px;
                            font-weight: 400;
                            line-height: normal;
                            text-decoration-line: underline;
                            border: none;
                            cursor: pointer;
                            padding: 0;
