import { Component, Input } from '@angular/core';
import { NgbTooltipModule } from '@ng-bootstrap/ng-bootstrap';
@Component({
  selector: 'app-annual-exposure-details',
  imports: [NgbTooltipModule],
  templateUrl: './annual-exposure-details.component.html',
  styleUrl: './annual-exposure-details.component.scss'
})
export class AnnualExposureDetailsComponent {
  @Input() annualExposure: number = 1200001.2;
  @Input() recommendedHedge: number = 720001;
  @Input() recommendedHedgePercent: number = 60;
  @Input() currentHedge: number = 0;
  @Input() currentHedgePercent: number = 0;
  @Input() avgHedgeRate: string = '--';
}

import { Component, Input, ViewChild } from '@angular/core';
import { RouterOutlet } from '@angular/router';
import { HeaderComponent } from "./risk-dashboard/components/header/header.component";
import { SidebarComponent } from "./risk-dashboard/components/sidebar/sidebar.component";
import { DashboardComponent } from "./risk-dashboard/components/dashboard/dashboard.component";
import { CommonModule } from '@angular/common';
import { HedgingProposalComponent } from './risk-dashboard/components/hedging-proposal/hedging-proposal.component';

@Component({
  selector: 'app-root',
  imports: [RouterOutlet, HeaderComponent, SidebarComponent, DashboardComponent,CommonModule,HedgingProposalComponent],
  templateUrl:'app.component.html',
  styleUrl:'app.component.scss',
})
export class AppComponent {
  @ViewChild(SidebarComponent) private sidebar!: SidebarComponent;
  @Input() ShowDashboard: boolean = false;
  @Input() Showhedging: boolean = false;
  title = 'angular-latest';
  openSidebar() {
    this.sidebar.openSidebar();
  }
  onToggleView(showHedging: boolean) {
    this.Showhedging = showHedging;
    this.ShowDashboard = !showHedging;
  }
}

import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-breadcrumb-wallet',
  imports: [],
  templateUrl: './breadcrumb-wallet.component.html',
  styleUrl: './breadcrumb-wallet.component.scss'
})
export class BreadcrumbWalletComponent {
  @Input() baseCurrency: string = 'images/ils-flag.svg';
  @Input() fromCurrency: string = 'flags/us.svg';
  @Input() cashFlowName: string = 'S.L ROBOTICS';
  @Input() monthRange: string = 'Jan 24 - Dec 24';
}

import { CommonModule } from '@angular/common';
import { Component, Input, Output, EventEmitter } from '@angular/core';

@Component({
  selector: 'storybook-button',
  standalone: true,
  imports: [CommonModule],
  template: ` <button
  type="button"
  (click)="onClick.emit($event)"
  [ngClass]="classes"
  [ngStyle]="{ 'background-color': backgroundColor }"
>
  {{ label }}
</button>`,
  styleUrls: ['./button.css'],
})
export class ButtonComponent {
  /** Is this the principal call to action on the page? */
  @Input()
  primary = false;

  /** What background color to use */
  @Input()
  backgroundColor?: string;

  /** How large should the button be? */
  @Input()
  size: 'small' | 'medium' | 'large' = 'medium';

  /**
   * Button contents
   *
   * @required
   */
  @Input()
  label = 'Button';

  /** Optional click handler */
  @Output()
  onClick = new EventEmitter<Event>();

  public get classes(): string[] {
    const mode = this.primary ? 'storybook-button--primary' : 'storybook-button--secondary';

    return ['storybook-button', `storybook-button--${this.size}`, mode];
  }
}

styleUrlsData.data:
.storybook-button {
  display: inline-block;
  cursor: pointer;
  border: 0;
  border-radius: 3em;
  font-weight: 700;
  line-height: 1;
  font-family: 'Nunito Sans', 'Helvetica Neue', Helvetica, Arial, sans-serif;
}
.storybook-button--primary {
  background-color: #555ab9;
  color: white;
}
.storybook-button--secondary {
  box-shadow: rgba(0, 0, 0, 0.15) 0px 0px 0px 1px inset;
  background-color: transparent;
  color: #333;
}
.storybook-button--small {
  padding: 10px 16px;
  font-size: 12px;
}
.storybook-button--medium {
  padding: 11px 20px;
  font-size: 14px;
}
.storybook-button--large {
  padding: 12px 24px;
  font-size: 16px;
}

