@import '../../../../../../assets/scss/variable';

::ng-deep {
    .adv-policy-wrapper {
        .progress-container {
            display: flex;
            align-items: center;
            gap: 8px;
            .stepper-progressbar {
              max-width: 98px;
              min-width: 98px;
              width: 100%;
              height: 12px;
              border-radius: 20px;
      
              .mdc-linear-progress__buffer {
                height: 100%;
              }
              
              .mdc-linear-progress__bar {
                height: 100%;
              }
      
                .mdc-linear-progress__bar-inner {
                  border-width: 12px;
                  border-radius: 20px;
                  border-color: #175CD3;
            
                  @media screen and (max-width: 1440px) {
                    border-width: 9px;
                  }
            
                  @media screen and (max-width: 1024px) {
                    border-width: 12px;
                  }
                }
            
              @media screen and (max-width: 1440px) {
                height: 9px;
              }
            
              @media screen and (max-width: 1024px) {
                height: 12px;
              }
            }
      
            .progress-count {
              color: #000;
              font-family: $he_heebo;
              font-size: 12px;
              font-weight: 700;
              line-height: 20px;
            }
            @media screen and (max-width:480px) {
              .stepper-progressbar{
                min-width: 60px;
                max-width: 60px;
                height: 9px;
              }
            }
        }
        .heading {
            color: #2947F2;
            font-family: $he_heebo;
            font-size: 30px;
            font-weight: 700;
            line-height: 38px;
            margin: 40px 0 34px;
        }

        .step1-wrapper {
            background-color: $white;
            padding: 50px 51px 36px 63px;
            border-radius: 10px;

            .step-form-wrap {
                text-align: center;

                .progress-container {
                    display: flex;
                    align-items: center;
                    gap: 8px;
                    justify-content: center;

                    .stepper-progressbar {
                        max-width: 98px;
                        min-width: 98px;
                        width: 100%;
                        height: 8px;
                        border-radius: 20px;

                        .mdc-linear-progress__bar-inner {
                            border-width: 8px;
                            border-radius: 20px;
                            border-color: #175CD3;
                            left: 0;
                        }
                    }

                    .progress-count {
                        color: #000;
                        font-family: $he_heebo;
                        font-size: 12px;
                        font-weight: 700;
                        line-height: 20px;
                    }

                    @media screen and (max-width:480px) {
                        .stepper-progressbar {
                            min-width: 60px;
                            max-width: 60px;
                        }
                    }
                }

                .anual-head {
                    margin: 42px 0 49px 0;

                    .sub-text {
                        color: #2947F2;
                        font-family: $he_heebo;
                        font-size: 14px;
                        font-weight: 700;
                        line-height: 20px;
                        margin: 0 0 2px;
                    }

                    .title {
                        color: #344054;
                        font-family: $he_heebo;
                        font-size: 30px;
                        font-weight: 700;
                        line-height: 38px;
                        margin: 0;
                    }
                    .desc{
                        color:#98A2B3;
                        text-align: center;
                        font-family:$he_heebo;
                        font-size: 14px;
                        font-weight: 400;
                        line-height: 18px;
                        margin: 2px 0 0;
                    }
                }

                .rate-wrap {
                    margin: auto;
                    max-width: 563px;

                    .inner-wrap {
                        display: flex;
                        gap: 14px;

                        .form-wrap {
                            display: flex;
                            position: relative;
                            border: 1px solid #3B82F6;
                            border-radius: 5px;
                            background-color: $white;
                            height: 60px;
                            align-items: center;
                            padding: 14px 15px 10px 17px;
                            width: 100%;

                            .rate-label {
                                position: absolute;
                                top: -7px;
                                left: 9px;
                                padding: 0 4px;
                                background-color: $white;
                                color: #3B82F6;
                                font-family: 'Roboto';
                                font-size: 12px;
                                font-weight: 400;
                                line-height: 16px;
                            }

                            .flag-wrap {
                                padding: 0 15px 0 0;
                                display: flex;
                                align-items: center;

                                img {
                                    width: 29px;
                                    height: auto;

                                    &.from-currency {
                                        margin: 0 0 0 -14px;
                                    }

                                    &.no-data {
                                        filter: grayscale(1);
                                        opacity: 0.6;
                                    }
                                }
                            }

                            .rate-input {
                                border: none;
                                padding: 0 12px;
                                border-left: 1px solid #E4E7EC;
                                font-family: $he_heebo;
                                font-size: 14px;
                                font-weight: 400;
                                line-height: 35px;
                                color: $black;
                                width: 100%;

                                &::placeholder {
                                    color: #B4BBC6;
                                }
                            }

                            .clear-btn {
                                padding: 0 0 4px;
                                line-height: 0;
                            }

                            &.errorInput {
