                        font-family: $he_heebo;
                        font-size: 14px;
                        font-weight: 400;
                        line-height: 22px;
                        margin: 9px 0 0;

                        span {
                            color: #003EFF;
                        }
                    }

                    .error-msg-wrap {
                        margin:7px 0 0;
                        color: #D92D20;
                        text-align: left;
                        font-family: $he_heebo;
                        font-size: 14px;
                        font-weight: 400;
                        line-height: 20px;
                    }
                }

                .rate-info {
                    max-width: 518px;
                    margin:44px auto 196px;

                    p {
                        margin: 0;
                        color: #98A2B3;
                        text-align: center;
                        font-family: $he_heebo;
                        font-size: 14px;
                        font-weight: 400;
                        line-height: 18px;
                    }
                }
            }

            .btn-wrap {
                display: flex;
                justify-content: space-between;
                align-items: center;

                .btn-policy {
                    display: flex;
                    height: 46px;
                    padding: 10px 20px;
                    justify-content: center;
                    align-items: center;
                    color: #7889B0;
                    font-family: $he_heebo;
                    font-size: 14px;
                    font-weight: 700;
                    line-height: 45.2px;
                    border-radius: 40px;
                    border: 1px solid #E4E4E5;
