                        font-family: $he_heebo;
                        font-size: 14px;
                        font-weight: 400;
                        line-height: 45.2px;
                        padding: 0;
                    }

                    .btn-next {
                        display: flex;
                        width: 123px;
                        height: 46px;
                        padding: 10px;
                        justify-content: center;
                        align-items: center;
                        gap: 10px;
                        border-radius: 40px;
                        background: #2947F2;
                        box-shadow: 0px 9px 24px 0px rgba(0, 62, 255, 0.40);
                        color: $white;
                        font-family: $he_heebo;
                        font-size: 14px;
                        font-weight: 700;
                        line-height: 45.2px;
                        &:hover{
                            opacity: 0.9;
                        }
                        &.disabled {
                            color: #9ca3af;
                            background: #f3f4f6;
                            box-shadow: none;

                            img {
                                filter: contrast(0.5);
                            }
                        }
                    }
                }
            }
        }

        @media screen and (max-width:1440px) {
            .heading {
                font-size: 20px;
                margin: 0 0 25px;
                line-height: 30px;
            }

            .step1-wrapper {
                padding: 30px;

                .step-form-wrap {
                    .anual-head {
                        margin:30px 0;

                        .title {
                            font-size: 24px;
                            line-height: 30px;
                        }
                    }

                    .rate-wrap {
                        .inner-wrap {
                            .form-wrap {
