                                    padding: 0 10px 0 0;

                                    img {
                                        width: 25px;

                                        &.from-currency {
                                            margin: 0 0 0 -10px;
                                        }
                                    }
                                }
                            }
                        }
                    }
                    .rate-info {
                        margin:30px auto;
                    }
                }
            }
        }

        @media screen and (max-width:1024px) {
            .heading {
                font-size: 18px;
                margin: 0 0 20px;
                line-height: 1.2;
            }

            .step1-wrapper {
                .step-form-wrap {
                    .anual-head {
                        .title {
                            font-size: 22px;
                            line-height: 1.2;
                        }
                    }

                    .rate-wrap {
                        .inner-wrap {
                            .form-wrap {
                                height: 50px;
                            }

                            .btn-generateRate {
                                height: 50px;
                                max-width: 110px;
                            }
                        }
                    }
                }

                .btn-wrap {
                    .btn-policy {
                        height: 36px;
                        line-height: 30px;
                        padding: 5px 15px;
                    }

                    .right-btn {
                        gap: 20px;

                        .btn-back {
                            line-height: 30px;
                        }

                        .btn-next {
                            width: 90px;
                            height: 36px;
                            line-height: 30px;
                        }
                    }
                }
            }
        }

        @media screen and (max-width:767px) {
            .step1-wrapper {
                .step-form-wrap {
                    .anual-head {
                        margin: 20px 0;

                        .title {
                            font-size: 20px;
                            line-height: 1.2;
                        }
                    }

                    .rate-wrap {

                        .generate-desc {
                            font-size: 12px;
                        }

                        .error-msg-wrap {
                            font-size: 12px;
                        }
                    }

                    .rate-info {
                        p {
                            font-size: 12px;
                        }
                    }
                }
            }
        }

        @media screen and (max-width:680px) {
            .step1-wrapper {
                .step-form-wrap {
                    .anual-head {
                        .sub-text,.desc {
                            font-size: 12px;
                        }

                        .title {
                            font-size: 18px;
                            line-height: 1.2;
                        }
                    }

                    .rate-wrap {
                        .inner-wrap {
                            .form-wrap {
                                height: 50px;
                                padding: 10px;

                                .flag-wrap {
                                    img {
                                        width: 20px;
                                    }
                                }
                            }
                        }

                        .error-msg-wrap {
                            margin:5px 0 0;
                            font-size: 11px;
                        }
                    }
                }

                .btn-wrap {
                    .btn-policy {
                        font-size: 12px;
                    }

                    .right-btn {
                        gap: 10px;

                        .btn-back {
                            font-size: 12px;
                        }

                        .btn-next {
                            font-size: 12px;
                        }
                    }
                }
            }
        }

        @media screen and (max-width:420px) {
            .step1-wrapper {
                padding: 20px;

                .step-form-wrap {
                    .anual-head {
                        .title {
                            font-size: 15px;
                        }
                    }
                    .rate-wrap {
                        .inner-wrap {
                            flex-direction: column;
                            align-items: flex-end;
                            gap: 10px;
                            .btn-generateRate{
                                font-size: 12px;
                                max-width: max-content;
                                height: 40px;
                            }
                        }
                    }
                }
            }
        }
    }

}

