export class AdvancedPolicyStep3Component implements OnInit {
  showfinalStep: boolean = false;
  router = inject(Router);
  @Input() step2Res = [
    {
        "index": 0,
        "answer": "5.0955"
    },
    {
        "index": 1,
        "answer": "High"
    },
    {
        "index": 2,
        "answer": "Medium"
    },
