  //               this.step2Res = [];
  //               this.step2Res.push({
  //                 index: 0,
  //                 answer: this.advancePolicyFromAPI.budgetRate
  //               });

  //               this.step2Res.forEach((item: any) => {
  //                 const index = item.advancePolicyQuestions;
  //                 if (index >= 0) {
  //                   this.step2Res.push({
  //                     index: index,
  //                     answer: this.mapIntegerToAnswer(item.score, index)
  //                   });
