  //               });
  //               this.step2Res = this.step2Res.sort((a: any, b: any) => a.index - b.index);
  //               this.riskManagerService.setAdvancePolicyStep2Formvalue(this.step2Res);
  //         //     }
  //           },
  //           error: (error) => {

