import { MatIconModule } from '@angular/material/icon';
import { getCalendarDataByDate } from '../../dashboard-data/calendar-data';
import { DashboardTableViewComponent } from '../dashboard-table-view/dashboard-table-view.component';

const calendarResources: MbscResource[] = [
  {
    id: 0,
    name: 'Rate Alert',
    color: '#F04853',
  },
  {
    id: 1,
    name: 'Payment',
    color: '#2947F2',
  },
  {
    id: 2,
    name: 'Exchange',
    color: '#01031C',
  },
  {
    id: 3,
    name: 'Hedge',
    color: '#FF6805',
  },
];

@Component({
  selector: 'app-calendar',
  imports: [
    CommonModule, 
    MatMenuModule, 
    MatSelectModule, 
    FormsModule, 
    MatIconModule,
    MbscModule,
    DashboardTableViewComponent
  ],
  templateUrl: './calendar.component.html',
  styleUrls: ['./calendar.component.scss'],
})
export class CalendarComponent {
  @Input() showTable = false;
  @Output() tableVisibilityChange = new EventEmitter<boolean>();
  @Input() resourceId = 0;
  startDate: any = new Date();
  endDate: any = new Date();
  eventData: any;
  flagData: any;
  data: any;
  selectedTab: any;
  selectedCalendarDate!: string | null;
  calendarMenu!: string;
  isMenu!: boolean;
  oldDate!: boolean;
  unSubScribe$ = new Subject<void>();
  affiliateCountry: any;
  @ViewChild('eventCalendar') eventCalendar!: MbscEventcalendar;
  @ViewChild('alertDrawer')
  alertDrawer!: MatDrawer;
  calendarData: CalendarDayObjModel[] = [];
  selectedCurrency: any;
  dayNotes: string | undefined;
  tempDayObj = <CalendarDayObjModel>{};
  tempEventObj = <CalendarEventsModel>{};
  tempNotesObj!: CalendarNotesModel;
  popupHeader!: string;
  isEdit = false;
  popupButtons: any = [];
  calendarOptions!: MbscEventcalendarOptions;
  resources = calendarResources;
  calendarConfigs = new CalendarConfigs();
  calendarView = 'week';
  view: any = {
    timeline: {
      type: this.calendarView,
      eventList: true,
    },
  };
  currencyData$!: Observable<any[]>;
  selectedCurrencies: any[] = ['all'];
  filteredCurrency$!: Observable<any[]>;
  walletCurrencies!: any[];
  filterControl = new FormControl('');
  activeCurrency: any;
  holidays: any[] = [
    {
      recurring: {
        repeat: 'weekly',
        weekDays: 'SA,SU',
      },
    },
  ];
  calendarLoad = false;
  showLoader = false;
  userRoleType!: number;
  user:any;

  constructor(
    private router: Router,
    public dialog: MatDialog,
    private renderer: Renderer2,
    private _walletService: WalletsService,
    private dashboardService: DashboardService,
    private cdr: ChangeDetectorRef
  ) { }

  ngOnInit() {
    this.activeCurrency = JSON.parse(localStorage.getItem('activeWallet')!);
    this.loadCalendarDataOnChange(new Date());

    this.calendarOptions = this.getCalendarOptions();
    this.currencyData$ = this._walletService.getAllBalanceList().pipe(
      tap((res) => {
        this.walletCurrencies = res;
        this.changeSearch();
      })
    );
    localStorage.setItem('calendarSelectedDate', moment().format('YYYY-MM-DD'));
  }

  ngDoCheck(){
    this.cdr?.detectChanges();
  }

  ngOnChanges(changes: any): void {
    if (changes?.resourceId) {
      this.tabSelect(changes?.resourceId?.currentValue);
    }
  }

  getCalendarOptions(): MbscEventcalendarOptions {
    if(this.user?.afiiliate?.currency == 'EUR'){
     let index =  this.resources?.findIndex((x:any)=> x.id === 3);
      if(index > -1){
        this.resources.splice(index,1);
      }
     }
    let calendarOptions = this.calendarConfigs.getCalendarOptions(this.calendarData, this.resources, this.view, this.holidays);
    let otherOptions: MbscEventcalendarOptions = {
      clickToCreate: 'single',
      eventDelete: false,
      onEventClick: (args: any) => { },
      onEventCreate: (args: any) => {
        return false;
      },
      onCellClick: (args: any, inst: any) => {
        if (this.calendarView !== 'month') {
          this.generateCellMenu(args);
        }
      },
      onPageLoaded: () => {
        this.setTimelineColumnsId();
      }
    };
    return { ...calendarOptions, ...otherOptions };
  }

  changeView(ev: any): void {
    this.showLoader = true;
    setTimeout(() => {
      switch (this.calendarView) {
        case 'day':
          this.view = {
            timeline: {
              type: 'day',
              eventList: true,
            },
          };
          break;
        case 'week':
          this.view = {
            timeline: {
              type: 'week',
              eventList: true,
            },
          };
          break;
        case 'month':
          this.view = {
            calendar: {
              type: 'month',
              eventList: true,
              labels: 'all',
              outerDays: false,
              // popover: true
            },
          };
          break;
      }
      this.calendarOptions = this.getCalendarOptions();
      localStorage.setItem('calendarSelectedDate', moment().format('YYYY-MM-DD'));
    });
  }

  createNote(date: string, resource: number) {
  }


  loadPopupForm(startDate: any, resource: number): void {
    this.tempEventObj = <CalendarEventsModel>{};
    // this.tempDayObj = {
    //   id: 1,
    //   start: moment(startDate).format('YYYY-MM-DD'),
    //   resource: resource,
    //   title: this.resources[resource]?.name,
    // };
  }

  generateCellMenu(args: any, event?: any) {
    document.querySelectorAll('#createEventCustMenu').forEach((item) => {
      item?.remove();
    });
    const cell = event?.target ? event.target : args.target;
    const customMenu = this.renderer.createElement('div');
    const currentDate = moment();
    this.oldDate = moment(args.date).isBefore(currentDate, 'day');
    customMenu.classList.add('custom-cell-menu');
    customMenu.id = 'createEventCustMenu';

    const eventButton = this.renderer.createElement('button');
    const noteButton = this.renderer.createElement('button');
    eventButton.setAttribute('data-date', moment(args.date).format('YYYY-MM-DD'));
    this.selectedCalendarDate = args.date;
    noteButton.setAttribute('data-date', moment(args.date).format('YYYY-MM-DD'));
    eventButton.setAttribute('data-resource', args.resource);
    noteButton.setAttribute('data-resource', args.resource);
    eventButton.setAttribute('id', `mbsc-add-event-cust-${args.resource}`);
    eventButton.classList.add('menu-btn-one');
    noteButton.classList.add('menu-btn-two');
    if (args.resource == 0) {
      eventButton.innerText = 'Add Alert';
    } else if (args.resource == 1) {
      eventButton.innerText = 'Add Payment';
    } else if (args.resource == 2) {
      eventButton.innerText = 'Add Exchange';
    } else if (args.resource == 3) {
      eventButton.innerText = 'Add Hedge';
    }
    noteButton.innerText = 'Add note';
    this.renderer.appendChild(customMenu, eventButton);
    this.renderer.listen(eventButton, 'click', (ev) => {
      if (args.resource == 0) {
        this.alertDrawerOpen(false, args?.date);
      } else if (args.resource == 1) {
        this.openPaymentDialog(false, args?.date);
      } else if (args.resource == 2) {
        this.openConvertDialog(false, args?.date);
      } else if (args.resource == 3) {
        this.openHedgeDialog(false, args?.date);
      }
    });
    this.renderer.listen(noteButton, 'click', (ev) => {
      const date = ev.currentTarget.getAttribute('data-date');
      const resource = ev.currentTarget.getAttribute('data-resource');
      this.createNote(date, resource);
    });
    if (!cell.querySelector('.custom-cell-menu') && !this.oldDate) {
      let body = document.querySelector('body');
      this.renderer.appendChild(body, customMenu);
      if (this.affiliateCountry !== 'il' && args.resource == 2 && moment(this.selectedCalendarDate, "YYYY-MM-DD").isAfter(currentDate)) {
        customMenu.style.display = 'none';  // Hides customMenu for future dates
      }
    }
    let rootPositionX = cell.getBoundingClientRect().x;
    let rootPositionY = cell.getBoundingClientRect().y;
    let menuWidth = customMenu.offsetWidth;
    let menuHeight = customMenu.offsetHeight;

    customMenu.style.left = `${rootPositionX - menuWidth / 2}px`;
    customMenu.style.top = `${rootPositionY - menuHeight / 2}px`;
    if(!this.oldDate) {
      document.querySelector('body')!.style.overflowY = 'hidden';
    }
    if(this.userRoleType !== 1 && (args.resource == 3 || args.resource == 2)){
      this.renderer.setProperty(eventButton, 'disabled', true);
    }
  }

  alertDrawerOpen(isMenu?: boolean, date?: any) {
    if (isMenu) {
      this.selectedCalendarDate = this.getSelectedDate();
    }
    if (date) {
      this.selectedCalendarDate = moment(date).format('YYYY-MM-DD');
    }
    this.alertDrawer?.open();
    this._walletService.setAlertData({ show: false, form: true });
    document.querySelector('body')!.style.overflowY = 'hidden';
  }
  alertDrawerClose() {
    this.alertDrawer?.close();
    document.querySelector('body')!.style.overflowY = 'auto';
    this.loadCalendarDataOnChange(new Date());
  }

  openPaymentDialog(isMenu?: boolean, date?: any) {
    let activeWallet: any = localStorage.getItem('activeWallet');
    let currency = JSON.parse(activeWallet);
    let currentDate = moment();
    if (isMenu) {
      this.selectedCalendarDate = this.getSelectedDate();
    }
    if (date) {
      this.selectedCalendarDate = moment(date).format('YYYY-MM-DD');
    }

  }

  openConvertDialog(isMenu?: boolean, date?: any) {
    if (isMenu) {
      this.selectedCalendarDate = this.getSelectedDate();
    }
    if (date) {
      this.selectedCalendarDate = moment(date).format('YYYY-MM-DD');
    }

  }

  openHedgeDialog(isMenu?: boolean, date?: any) {
  }

  confirmAlertDelete() {
  }

  onSelectionChange(ev: any) {
    if (ev?.options[0]?._value !== 'all') {
      this.selectedCurrencies = [...ev?.source?._value];
      if (this.selectedCurrencies.indexOf('all') !== -1) {
        this.selectedCurrencies.splice(this.selectedCurrencies.indexOf('all'), 1);
      }
    } else {
      this.selectedCurrencies = ['all'];
    }
  }

  private _filter(value: string): any[] {
    return this.walletCurrencies.filter((currency) => currency.wallet_Currency.code.toLowerCase().includes(value.toLowerCase()));
  }
  changeSearch() {
    this.filteredCurrency$ = this.filterControl.valueChanges.pipe(
      startWith(''),
      map((val) => {
        return this._filter(val || '');
      })
    );
  }

  tabSelect(id: number) {
    this.selectedTab = id;
  }

  getHolidays() {
    let FromDate = moment(new Date()).format(DateFormat?.dateInput);
    let ToDate = moment().add(1, 'year').format(DateFormat?.dateInput);
    let currency = this.activeCurrency?.wallet_Currency?.code;
    this.showLoader = true;
  }

  getSelectedDate() {
    return localStorage.getItem('calendarSelectedDate');
  }

  setTableVisibility(visible: boolean) {
    this.showTable = visible;
    this.tableVisibilityChange.emit(visible);
  }

  getCalendarData(fromDate: string, toDate: string) {
    this.showLoader = true;
    of(getCalendarDataByDate).pipe(takeUntil(this.unSubScribe$)).subscribe((result) => {
      if (result) {
        this.showLoader = false;
        // this.changeCalendarDataFormat(result);
        this.calendarData = result.map((item: any) => {
          let changedFormatDate = item.start.split('/').reverse().join('/');
          item.start = moment(changedFormatDate).format('YYYY-MM-DD');
          item.events = item.events.filter((event: any) => event.status !== 'Deleted');
          return item;
        }).filter((item: any) => item?.events?.length);
        this.calendarData.sort(this.sortByDate);
        this.getHolidays();
      }
    });
  }

  sortByDate(aDate: any, bDate: any) {
    const dateA: any = new Date(aDate.start);
    const dateB: any = new Date(bDate.start);
    return dateA - dateB;
  }

  calendarPageChanged(ev: any) {
    this.loadCalendarDataOnChange(ev.firstDay);
    setTimeout(() => {
      document.querySelectorAll('.mbsc-timeline-header .mbsc-timeline-header-bg .mbsc-timeline-day').forEach((dayItem) => {
        dayItem.addEventListener('click', (ev) => {
          let currentTarget = ev?.currentTarget as HTMLElement;
          let dateElement = currentTarget.querySelector('.mbsc-hidden-content');
          let selectedDate = moment(dateElement?.innerHTML).format('YYYY-MM-DD');
          let holidays = JSON.parse(localStorage.getItem('holidays')!);
          const isWeekend = moment(selectedDate).day() === 0 || moment(selectedDate).day() === 6;
          if (!holidays.includes(selectedDate) && !isWeekend && this.isDayDisabled(dateElement?.innerHTML)) {
            localStorage.setItem('calendarSelectedDate', selectedDate);
            document.querySelectorAll('.mbsc-timeline-header .mbsc-timeline-header-bg .mbsc-timeline-day .mbsc-timeline-header-date .date-label').forEach((item) => {
              item.classList.remove('today');
              currentTarget.querySelector('.date-label')!.classList.add('today');
            });
          }
        });
      });
    }, 500);
  }

  calendarDateChanged(ev: any) {
    let selectedDate = moment(ev?.date).format('YYYY-MM-DD');
    localStorage.setItem('calendarSelectedDate', selectedDate);
  }

  loadCalendarDataOnChange(date: any) {
    let timeUnit: 'month' | 'week' = this.calendarView == 'month' ? 'month' : 'week';
    const currentDate = date ? moment(date) : moment();
    const startDate = currentDate.clone().startOf(timeUnit).format('DD/MM/YYYY');
    const endDate = currentDate.clone().endOf(timeUnit).format('DD/MM/YYYY');
    this.getCalendarData(startDate, endDate);
  }

  getData(data: any) {
    this.flagData = data?.events ? data?.events[0] : '';
  }

  GetAlertData(data: any) {
    this.flagData = data;
  }

  DeleteAlert(id: any) {

  }

  checkHoliday(date: any) {
    const currentDate = moment(); // Get the current date
    const isWeekend = moment(date).day() === 0 || moment(date).day() === 6;
    const convertedDate = moment(date).format('YYYY-MM-DD');
    const isPastDate = moment(date).isBefore(currentDate, 'day');
    this.oldDate = isPastDate;
    return !this.holidays.includes(convertedDate) && !isWeekend && !isPastDate;
  }

  hasEvent(date: any) {
    return this.calendarData.some((obj) => obj['start'] === moment(date).format('YYYY-MM-DD'));
    // console.log(date);
  }

  isDayDisabled(date: any) {
    const isWeekend = moment(date).day() === 0 || moment(date).day() === 6;
    const convertedDate = moment(date).format('YYYY-MM-DD');
    const currentDate = moment().format('YYYY-MM-DD');
    const isPastDate = currentDate < convertedDate;
    this.oldDate = isPastDate;
    return !this.holidays.includes(convertedDate) && !isWeekend && isPastDate;
  }

  setTimelineColumnsId() {
    document.querySelectorAll('.cust-event-calendar .cust-event-calendar .mbsc-timeline-column').forEach((element, index) => {
      element.setAttribute('id', `event-add-area-${index}`);
    });
  }

  ngOnDestroy() {
    document.querySelectorAll('#createEventCustMenu').forEach((item) => {
      item?.remove();
    });
    this.unSubScribe$.next();
    this.unSubScribe$.complete();
  }
}

styleUrlsData.data:
@import "../../../../assets/scss/variable";
@import "../../../../assets/scss/mixins";

.country-dropdown {
  font-size: $font-16;
  line-height: 20px;
  color: $light-gray !important;
  font-weight: 600;
  font-family: "$inter";
  padding-right: 20px;

  .currency-image {
    margin-right: 10px;
  }
}

.img-group {
  position: relative;
  padding-top: 10px;
  padding-left: 10px;
  z-index: 1;

  @media only screen and (max-width: 1440px) {
    padding-top: 2px;
  }

  .active_currency_flag {
    width: 28px;
    height: 28px;
    border-radius: 28px;
    position: relative;
  }

  .user_currency_flag {
    width: 20px;
    height: 20px;
    border-radius: 20px;
    position: absolute;
    left: 0;
    top: 0;
    z-index: -1;
  }
}

.label-group {
  .cpr-title {
    color: #01031c;
    font-size: 18px;
    font-weight: 700;
    line-height: 24px;
    margin-bottom: 0;

    @media only screen and (max-width: 1440px) {
      font-size: 16px;
    }
  }
}

.calendar-box-wrapper {
  height: 658px;
  max-height: 658px;
  overflow-y: hidden;

  @media screen and (max-width: 1440px) {
    height: 100%;
    max-height: 539px;
    overflow-y: auto;
  }

  // @media screen and (max-width: 1024px) {
  //   min-height: 537px;
  // }

  // @media screen and (max-width: 767px) {
  //   min-height: 497px;
  // }
}

::ng-deep {
  .cust-event-calendar {
    font-family: $manrope;

    .mbsc-calendar-controls {
      padding: 0;

      .header-wrapper {
        @include flex(space-between, center, row);
        width: 100%;
        border-radius: 3px;
        background: var(--content-wrapper-bg-color);

        @media only screen and (max-width: 380px) {
          flex-wrap: wrap;
          row-gap: 10px;
        }

        .calender-header-left {
          @include flex(flex-start, center, row);
          column-gap: 6px;
          margin-bottom: 10px;

          .date-change {
            @include flex(space-between, center, row);
            width: 196px;
            height: 50px;
            border-radius: 7px;
            background: $white;
            padding: 10px;

            @media only screen and (max-width: 1440px) {
              height: 40px;
            }

            @media only screen and (max-width: 480px) {
              width: 175px;
            }

            .md-work-week-prev,
            .md-work-week-next {
              width: 20px;
              height: 20px;
              display: flex;
              align-items: center;

              .mbsc-calendar-button {
                &.mbsc-button {
                  &.mbsc-reset {
                    padding: 0;
                    width: fit-content;
                    height: fit-content;
                  }
                }
              }

              mbsc-icon.mbsc-icon {
                width: 20px;
                height: 20px;
              }
            }

            .mbsc-windows.mbsc-icon-button {
              width: 20px;
              height: 20px;
              color: $calender-arrow-icon-color !important;
              padding: 0;

              svg {
                width: 15px;

                @media only screen and (max-width: 1440px) {
                  width: 10px;
                }
              }
            }

            .md-work-week-nav {
              mbsc-button {
                padding: 0px;

                span.mbsc-calendar-title {
                  color: $tab-color;
                  font-size: $font-14;
                  font-style: normal;
                  font-weight: 700;
                  line-height: 18px;

                  @media only screen and (max-width: 1440px) {
                    font-size: 12px;
                  }

                  @media (max-width: 576px) {
                    line-height: 20px;
                  }
                }
              }
            }
          }

          .md-work-week-picker {
            width: 87px;
            height: 51px;
            padding: 15px 13px;
            border-radius: 7px;
            background: $white;

            @media only screen and (max-width: 1440px) {
              width: 74px;
              height: 40px;
              padding: 7px 9px;
            }

            mat-select {
              .mat-mdc-select-value {
                span.mat-mdc-select-value-text {
                  color: var(--light-blue);
                  font-size: $font-14;
                  font-style: normal;
                  font-weight: 700;
                  line-height: 20px;

                  @media only screen and (max-width: 1440px) {
                    font-size: 12px;
                    line-height: 16px;
                  }
                }
              }

              .mat-mdc-select-arrow-wrapper {
                .mat-mdc-select-arrow {
                  svg {
                    background-image: url(../../../../assets/images/drop-down-arrow-calender.svg);
                    background-repeat: no-repeat;
                    background-position: center center;
                    background-size: 10px;

                    @media only screen and (max-width: 1440px) {
                      background-size: 7px;
                      top: 86%;
                    }

                    path {
                      display: none;
                    }
                  }
                }
              }
            }
          }
        }

        .calender-header-right {
          @include flex(center, center, row);
          column-gap: 14px;
          margin-bottom: 8px;

          button.clander-view-btn {
            @include flex(center, center, row);
            width: 51px;
            height: 51px;
            padding: 10px;
            border-radius: 7px;

            @media only screen and (max-width: 1440px) {
              width: 40px;
              height: 40px;
              padding: 7px;
            }

            &.clander-view-btn-graph {
              background-color: $white;
              background-image: url(../../../../assets/images/graph-view-new-icon.svg);
              background-repeat: no-repeat;
              background-position: center center;
              background-size: 22px;

              @media only screen and (max-width: 1440px) {
                background-size: 18px;
              }
            }

            &.clander-view-btn-plush {
              background: var(--calender-plush-icon-background);
              border-radius: 3px;
              background-image: url(../../../../assets/images/plush-sing.svg);
              background-repeat: no-repeat;
              background-position: center center;
              background-size: auto;

              @media only screen and (max-width: 1440px) {
                background-size: 14px;
              }
            }
          }
        }
      }
    }

    .cust-event-calendar .mbsc-timeline-day {
      min-width: 122px;

      @media only screen and (max-width: 1440px) {
        min-width: 110px;
      }
    }

    .mbsc-timeline-event.mbsc-schedule-event-all-day {
      padding-left: 0;
      padding-right: 0;
    }

    .mbsc-timeline-event.mbsc-schedule-event-all-day .cell-event {
      height: 100%;
      display: flex;
      flex-direction: column;
      justify-content: space-between;
    }

    .cust-event-label {
      @include flex(space-between, center, row);
      padding: 5px 12px;
      min-height: 34px;
      margin: 7px 0 0;
      border-left: 3px solid;
      width: 100%;
      max-width: 100%;

      &[aria-disabled="true"] {
        pointer-events: none;
      }

      &.ev-0 {
        border-color: $cal-ev-red;
        background: #fef1f2;
        color: $cal-ev-red;

        .label {
          color: $cal-ev-red;
        }

        .count {
          color: $cal-ev-red;
        }
      }

      &.ev-1 {
        border-color: var(--cal-ev-blue);
        background: #f1f3fe;
        color: var(--cal-ev-blue);

        .label {
          color: var(--cal-ev-blue);
        }

        .count {
          color: var(--cal-ev-blue);
        }
      }

      &.ev-2 {
        border-color: $cal-ev-black;
        background: $new-white-background;
        color: $cal-ev-black;

        .label {
          color: $cal-ev-black;
        }

        .count {
          color: $cal-ev-black;
        }
      }

      &.ev-3 {
        border-color: $cal-ev-orange;
        color: $cal-ev-orange;
        background: rgba(255, 104, 5, 0.04);

        .label {
          color: $cal-ev-orange;
        }

        .count {
          color: $cal-ev-orange;
        }
      }

      &.today {
        background-color: $disable-button-bg;
      }

      .label {
        font-size: $font-13;
        line-height: 20px;
        font-weight: 600;
        color: $cal-ev-red;

        @media only screen and (max-width: 1440px) {
          font-size: 12px;
        }
      }

      .count {
        @include flex(center, center, row);
        font-size: $font-12;
        font-weight: 700;
        width: 35px;
        height: 28px;
        border-radius: 30px;
        background-color: $white;

        @media only screen and (max-width: 1440px) {
          width: 25px;
          height: 25px;
        }
      }
    }

    .date-label {
      @include flex(center, center, column);
      padding: 18px 20px;
      // background-color: #F5F7FE;
      @media only screen and (max-width: 1440px) {
        padding: 13px 20px;
      }

      &.today {
        // background-color: #fff;
        // border-top: 2px solid var(--brand-primary-color);
        // border-bottom: 3px solid #f5f7fe;
        // background: linear-gradient(180deg, #fff 0%, rgba(255, 255, 255, 0) 100%);
        border-bottom: 2px solid var(--brand-primary-color);
        max-height: 82px;

        @media (max-width: 1440px) {
          max-height: 64px;
        }

        .week-day {
          color: var(--light-blue);
          font-weight: 700;
        }

        .month-day {
          position: relative;
          color: var(--light-blue);
        }
      }

      &.has-event {
        .month-day {
          &.week {
            position: relative;
            &::before {
              content: "";
              position: absolute;
              height: 5px;
              width: 5px;
              background-color: var(--light-blue);
              left: 0;
              right: 0;
              bottom: -6px;
              margin: auto;
              border-radius: 10px;
            }
          }
        }
      }

      &.disabled {
        cursor: not-allowed;
        .month-day {
          color: #848585;
        }
      }

      .week-day {
        color: $week-day-gray;
        font-size: $font-14;
        font-weight: 400;

        @media only screen and (max-width: 1440px) {
          font-size: 12px;
        }
      }

      .month-day {
        color: $black;
        font-size: $font-18;
        line-height: 25px;
        font-weight: 700;

        @media only screen and (max-width: 1440px) {
          font-size: 14px;
          line-height: 20px;
        }
      }
    }

    .resource-wrapper {
      @include flex(flex-start, flex-start, column);
      padding-left: 10px;

      .resource-name {
        position: relative;
        font-size: $font-14;
        line-height: 18px;
        font-weight: 700;
        color: $blue-black;

        &.rs-alert {
          &:before {
            background-color: $cal-ev-red;
          }
        }

        &.rs-payment {
          &:before {
            background-color: var(--cal-ev-blue);
          }
        }

        &.rs-convert {
          &:before {
            background-color: $cal-ev-black;
          }
        }

        &.rs-hedge {
          &:before {
            background-color: $cal-ev-orange;
          }
        }

        &:before {
          content: "";
          position: absolute;
          height: 100%;
          width: 2px;
          top: 0;
          left: -9px;
          background-color: $cal-ev-red;
          border-radius: 5px;
        }
      }

      .view-all {
        font-size: $font-12;
        line-height: 16px;
        text-decoration: underline !important;
        color: var(--cal-ev-blue);
        &:hover {
          cursor: pointer;
        }
      }
    }

    .mbsc-timeline-column {
      &:hover {
        cursor: pointer;
        &::before {
          visibility: visible;
        }

        &::after {
          visibility: visible;
        }
      }

      &::before {
        content: "+";
        position: absolute;
        top: 0;
        bottom: 0;
        left: 0;
        right: 0;
        margin: auto;
        border: 1px solid $color-blue;
        font-weight: bold;
        color: $color-blue;
        max-width: 26px;
        max-height: 26px;
        display: flex;
        align-items: center;
        justify-content: center;
        border-radius: 30px;
        visibility: hidden;

        @media only screen and (max-width: 1440px) {
          top: calc(50% - 15px);
        }
      }

      &::after {
        content: "Add";
        position: absolute;
        top: calc(50% - 30px);
        left: 50%;
        transform: translate(-50%, -50%);
        width: max-content;
        margin: auto;
        background: $black;
        color: $white;
        border-radius: 30px;
        padding: 3px 7px;
        font-size: $font-12;
        visibility: hidden;

        @media only screen and (max-width: 1440px) {
          top: calc(50% - 10px);
        }
      }
    }

    .mbsc-timeline-grid {
      height: 100% !important;
      .mbsc-timeline-row {
        &:nth-child(2) {
          .mbsc-timeline-column {
            &::after {
              content: "Add Alert";
            }
          }
        }

        &:nth-child(3) {
          .mbsc-timeline-column {
            &::after {
              content: "Add Payment";
            }
          }
        }

        &:nth-child(4) {
          .mbsc-timeline-column {
            &::after {
              content: "Add Exchange";
            }
          }
        }

        &:nth-child(5) {
          .mbsc-timeline-column {
            &::after {
              content: "Add Hedge";
            }
          }
        }
      }
    }

    .mbsc-calendar-labels {
      @include flex(center, center, row);
    }

    .mbsc-calendar-row {
      min-height: 120px !important;
    }

    .mbsc-timeline-column,
    .mbsc-timeline-resource.mbsc-ltr,
    .mbsc-timeline-header-date.mbsc-ltr,
    .mbsc-timeline-resource-header-cont.mbsc-ltr {
      border-right-style: dashed;
      border-color: $calendar-border-color;
    }

    .mbsc-timeline-header-date {
      border-bottom-style: none !important;
    }

    .mbsc-timeline-row,
    .mbsc-timeline-resource-header-cont,
    .mbsc-timeline-header-date {
      border-bottom-style: dashed;
      border-color: $calendar-border-color;
    }

    .mbsc-windows.mbsc-calendar-cell {
      border-style: dashed;
    }

    .mbsc-windows .mbsc-calendar-day {
      &.mbsc-selected {
        &::after {
          border-width: 0;
          border-left-width: 1px;
        }
      }

      &::after {
        border-left-style: dashed;
      }
    }

    .mbsc-timeline-resource-header-cont.mbsc-ltr,
    .mbsc-windows.mbsc-timeline-header-date {
      border: none !important;
      // height: 76px !important;
      background: $calender-header-color !important;

      @media only screen and (max-width: 1440px) {
        height: 65px !important;
      }
    }

    .mbsc-timeline {
      &.mbsc-ltr {
        padding: 10px;

        .mbsc-timeline-header-sticky {
          top: 10px;
          left: 10px;

          .mbsc-timeline-resource-header-cont {
            display: flex;
            align-items: center;
            // justify-content: center;

            .mbsc-timeline-resource-header {
              .all-currency-tab-wrap {
                display: flex;
                align-items: center;
                justify-content: center;
                column-gap: 2px;

                color: #000;
                font-family: $inter;
                font-size: 14px;
                font-weight: 400;
                line-height: 20px;
                text-transform: capitalize;

                outline: none;
                border: none;
                background: $white;
                padding: 10px;
                cursor: pointer;
                border-radius: 4px;
              }

              .all-currency-flag-new-wrap {
                padding: 5px 5px 5px 7px;
              }

            }
          }
        }
      }
    }

    .mbsc-timeline-resource-col,
    .mbsc-timeline-sidebar-col {
      width: 122px;

      @media (max-width: 1440px) {
        width: 110px;
      }
      @media (max-width: 479px) {
        width: 110px;
      }
    }

    .mbsc-timeline-resource-col {
      z-index: 5;

      @media (max-width: 479px) {
        width: 90px;
      }
    }

    .mbsc-timeline-resource.mbsc-ltr {
      display: flex;
      align-items: center;
    }

    .mbsc-timeline-resource.mbsc-ltr,
    .cust-event-calendar .mbsc-timeline-column {
      width: 122px;
      min-height: 125px !important;
      height: 100%;

      @media only screen and (max-width: 1440px) {
        width: 110px;
        min-height: 100px !important;
        max-height: 100px;
      }

      @media only screen and (max-width: 767px) {
        min-height: 90px !important;
      }

      @media (max-width: 479px) {
        width: 90px;
      }
    }

    .cust-event-calendar .mbsc-timeline-row {
      min-height: 125px !important;
      max-height: 125px;

      @media only screen and (max-width: 1440px) {
        min-height: 100px !important;
        max-height: 100px;
      }

      @media (max-width: 479px) {
        min-height: 90px !important;
        max-height: 90px;
      }
    }

    .currency-select-menu {
      @include flex(flex-start, center, row);
      height: 49px;
      width: 72px;
      border-radius: 8px;
      // border: 1px solid $general-border-color;
      background-color: $disable-button-bg;
      margin-left: auto;
      margin-right: auto;
      position: relative;
      padding-left: 0;

      .flag-group {
        @include flex(flex-start, center, row);
        position: relative;

        img {
          width: 24px;
          height: 24px;
          margin-left: -10px;
          border: 2px solid #fff;
        }

        .more-flags {
          position: absolute;
          top: 0;
          bottom: 0;
          right: 0;
          margin: auto 0;
          font-size: 9px;
          font-weight: 600;
          color: $black;
          background-color: $white;
          border-radius: 20px;
          height: 14px;
          width: 14px;
        }
      }

      img {
        width: 24px;
        height: 24px;
        border-radius: 50px;

        &.all-currency {
          width: 44px;
        }
      }

      &::before {
        content: "";
        position: absolute;
        width: 7px;
        height: 7px;
        border-left: 2px solid $dark-gray-border-color;
        border-top: 2px solid $dark-gray-border-color;
        transform: rotate(225deg);
        top: 0;
        bottom: 0;
        right: 13px;
        margin: auto;
      }
    }

    .mbsc-schedule-invalid {
      z-index: 2;
      cursor: not-allowed;
      background-color: $white;
      // border-right: 1px dashed $calendar-border-color !important;
      opacity: 0.5;

      &::before {
        content: "";
        background: url(../../../../assets/images/calender-disable-day.svg) center no-repeat;
        background-size: cover;
        position: absolute;
        top: 0;
        bottom: 0;
        left: 0;
        right: 0;
        border-right: 1px dashed $calendar-border-color;
      }
    }

    &.month-calender-wrap {
      position: relative;

      .cust-event-calendar {
        overflow: auto;
        position: static;

        .mbsc-calendar-day-inner {
          position: relative;

          &:hover {
            .month-add-event-btn {
              display: block;
              top: 0 !important;
            }
            .date-label {
              visibility: hidden;
            }
          }

          .month-add-event-btn {
            width: 25px;
            height: 25px;
            border-radius: 50px;
            border: 1px solid $color-blue;
            background: url(../../../../assets/images/plus-icon-blue.svg);
            background-repeat: no-repeat;
            background-position: center;
            position: absolute;
            top: 0;
            bottom: 0;
            left: 0;
            right: 0;
            margin: auto;
            display: none;
          }
        }

        .mbsc-calendar-wrapper {
          min-width: 735px;
          position: static;

          .mbsc-calendar-header {
            position: static;
            padding-top: 62px;
            background-color: #fff !important;
            border-color: transparent;

            @media (max-width: 1440px) {
              padding-top: 51px;
            }

            @media (max-width: 359px) {
              padding-top: 110px;
            }

            .mbsc-calendar-controls {
              position: absolute;
              left: 0;
              right: 0;
              top: 0;
            }

            .mbsc-calendar-week-days {
              margin-left: 144px;
              padding: 10px 9px 0;
              background-color: #fff !important;

              @media (max-width: 1279px) {
                margin-left: 120px;
              }

              @media (max-width: 767px) {
                margin-left: 100px;
              }

              .mbsc-calendar-week-day {
                display: flex;
                align-items: center;
                justify-content: center;
                min-height: 76px;
                background-color: #f6f8fe;

                @media (max-width: 479px) {
                  font-size: 12px;
                  line-height: 1.5;
                }

                @media (max-width: 359px) {
                  min-height: 55px;
                }
              }
            }
          }

          .mbsc-calendar-body {
            position: relative;
            padding: 25px 20px 30px;

            @media (max-width: 1440px) {
              padding: 15px;
            }

            @media (max-width: 767px) {
              padding: 10px;
            }

            .mbsc-calendar-body-inner {
              margin-left: 144px;

              @media (max-width: 1279px) {
                margin-left: 120px;
              }

              @media (max-width: 767px) {
                margin-left: 100px;
              }

              .mbsc-calendar-picker {
                .mbsc-calendar-table {
                  .mbsc-calendar-row {
                    min-height: 60px !important;

                    &:first-child {
                      .mbsc-calendar-day {
                        border-top-color: transparent;
                      }
                    }

                    .mbsc-calendar-day {
                      border-color: #d9dadd;

                      .mbsc-calendar-day-inner {
                        .month-add-event-btn {
                          top: 24px;
                          width: 22px;
                          height: 22px;
                          padding: 0;

                          @media (max-width: 1440px) {
                            top: 21px;
                            width: 20px;
                            height: 20px;
                          }
                        }

                        .date-label {
                          padding: 10px 15px 20px;

                          .month-day {
                            @media (max-width: 1440px) {
                              width: 28px;
                              height: 28px;
                            }
                          }
                        }
                      }

                      .mbsc-calendar-labels {
                        position: absolute;
                        bottom: 5px;
                        left: 0;
                        right: 0;
                        column-gap: 12px;

                        @media (max-width: 1440px) {
                          column-gap: 7px;
                        }

                        .mbsc-calendar-custom-label {
                          width: 7px;
                          padding: 0;
                          margin: 0;

                          .mat-mdc-menu-trigger {
                            padding: 3px;
                            width: 7px;
                            height: 7px;
                          }
                        }
                      }

                      &::after {
                        border-left-width: 0;
                      }

                      &.mbsc-disabled {
                        position: relative;
                        background-color: #ffffff;
                        color: #b4b6bb;

                        &::before {
                          content: "";
                          background: url(../../../../assets/images/calender-disable-day.svg) center no-repeat;
                          background-size: cover;
                          position: absolute;
                          top: 0;
                          bottom: 0;
                          left: 0;
                          right: 0;
                          z-index: 1;
                        }
                      }
                    }
                  }
                }
              }
            }
          }
        }
      }
    }
    .mbsc-has-sticky .mbsc-timeline-grid-scroll.mbsc-ltr {
      max-height: 576px;
      height: 100%;
      overflow-y: hidden;

      @media screen and (max-width: 1440px) {
        max-height: 465px;
        height: 100%;
      }

      @media screen and (max-width: 479px) {
        max-height: 100%;
        height: 100%;
      }
    }
    .mbsc-ios.mbsc-timeline-header-bg,
    .mbsc-material.mbsc-timeline-header-bg,
    .mbsc-timeline-header-bg.mbsc-windows {
      background: #f5f7fe !important;
      .mbsc-timeline-day {
        .mbsc-timeline-header-date {
          border-right-style: none;
        }
      }
    }
    .mbsc-calendar-wrapper {
      border-bottom: 0 !important;
    }
  }

  .month-view-resources {
    background-color: #ffffff;
    width: 166px;
    position: absolute;
    top: 72px;
    left: 0;
    bottom: 10px;
    z-index: 2;
    padding-left: 9px;

    @media (max-width: 1440px) {
      top: 61px;
      width: 153px;
    }

    @media (max-width: 1279px) {
      width: 129px;
    }

    @media (max-width: 767px) {
      width: 109px;
    }

    @media (max-width: 359px) {
      top: 121px;
    }

    .resource-header {
      min-height: 76px;
      background-color: #f6f8fe;
      display: flex;
      align-items: center;
      padding-left: 10px;

      @media (max-width: 359px) {
        padding-left: 7px;
        min-height: 55px;
      }

      span {
        display: flex;
        align-items: center;
        // justify-content: center;
        padding: 5px;
        // background-color: #fff;
        width: 100px;
        height: 48px;

        color: #000;
        font-family: $inter;
        font-size: 14px;
        font-weight: 400;
        line-height: 20px;

        @media (max-width: 767px) {
          width: 84px;
        }

        @media (max-width: 359px) {
          height: 40px;
        }
      }
    }

    .resource-body {
      display: flex;
      flex-direction: column;
      row-gap: 42px;
      margin: 38px 31px 0 31px;
      border-right: 1px dashed #d9dadd;
      height: calc(100% - 143px);

      @media (max-width: 1440px) {
        margin-left: 20px;
        margin-right: 20px;
      }

      @media (max-width: 1279px) {
        margin-left: 10px;
        margin-right: 10px;
      }

      div {
        color: #5b5b66;
        font-family: $font-1;
        font-size: 12px;
        font-weight: 400;
        line-height: normal;
        position: relative;

        padding-left: 12px;

        &.rs-alert {
          &::before {
            background-color: #f04853;
          }
        }

        &.rs-payment {
          &::before {
            background-color: var(--brand-primary-color);
          }
        }

        &.rs-convert {
          &::before {
            background-color: #01031c;
          }
        }

        &.rs-hedge {
          &::before {
            background-color: #ff6805;
          }
        }

        &::before {
          content: "";
          width: 7px;
          height: 7px;
          position: absolute;
          top: 0;
          left: 0;
          bottom: 0;
          margin: auto 0;
          border-radius: 7px;
          -webkit-border-radius: 7px;
          -moz-border-radius: 7px;
          -ms-border-radius: 7px;
          -o-border-radius: 7px;
        }
      }
    }
  }

