  .create-event-modal {
    .mbsc-windows-full {
      background-color: $white;
      height: auto !important;
      margin-top: auto;
      margin-bottom: auto;
    }
  }

  .add-event-month {
    height: 35px;
    width: 35px;
    border-radius: 50px;
    border: 1px solid $color-blue;
  }

  .event-dot {
    width: 10px;
    height: 10px;
    border-radius: 50%;
    position: absolute;
    left: 0;
    top: 7px;

    &[aria-disabled="true"] {
      pointer-events: none;
    }

    &.event-dot-0 {
      background-color: $cal-ev-red;
    }

    &.event-dot-1 {
      background-color: var(--cal-ev-blue);
    }

    &.event-dot-2 {
      background-color: $cal-ev-black;
    }

    &.event-dot-3 {
      background-color: $cal-ev-orange;
    }
  }

  .mbsc-calendar-day {
    .date-label {
      .month-day {
        @include flex(center, center, row);
        width: 32px;
        height: 32px;
        border-radius: 50px;
        color: $month-day-font-color;
        font-size: $font-14;
        line-height: 18px;
      }
    }

    &.mbsc-selected {
      .mbsc-calendar-cell-inner {
        background: transparent;
      }

      .date-label {
        .month-day {
          background-color: var(--light-blue);
          color: $white;
          font-weight: bold;
        }
      }
    }
  }

  .mdc-menu-surface {
    &.add-event-menu {
      border-radius: 10px;

      .mat-mdc-menu-content {
        padding-left: 25px;
        padding-right: 25px;

        .add-event-menu-item {
          border-bottom: 1px dashed rgba(29, 29, 34, 0.1);
          padding-top: 7px;
          padding-bottom: 7px;

          &:last-child {
            border-bottom: 0;
          }

          button {
            border-radius: 50px;
            min-height: 40px;
            padding: 8px 15px;
            position: relative;

            &.mat-mdc-menu-item {
              border-bottom: 0;
            }

            &.mat-mdc-menu-item[disabled] {
              color: #000 !important;
            }

