    .colse-back-white {
      padding: 17px 23px 34px;

      @media only screen and (max-width: 1440px) {
        padding: 10px;
      }

      button.mat-mdc-button-base {
        display: flex;
        width: 33px;
        height: 33px;
        justify-content: center;
        align-items: center;
        border-radius: 4px;
        border: 1px solid rgba(1, 3, 28, 0.1);
        background: $white;
        padding: 0;

        @media only screen and (max-width: 1440px) {
          width: 25px;
          height: 25px;
        }
      }
    }
  }

  .cell-actions {
    @include flex(flex-end, flex-start, row);
    padding: 8px;

    button.mat-mdc-button-base {
      width: 28px;
      height: 28px;
      padding: 3px;
    }
  }

  .add-event-btn {
    height: 20px !important;
    width: 20px !important;
    border: 1px solid $color-blue !important;
    background-image: url(../../../../assets/images/plus-icon-blue.svg);
    background-repeat: no-repeat;
    background-position: center;
    border-radius: 30px;
  }

  .note-btn {
    height: 20px;
    width: 20px;
    background-image: url(../../../../assets/images/note-icon-dark.svg);
    background-repeat: no-repeat;
    background-position: center;
  }

  .notes-list-menu {
    &.mat-mdc-menu-panel.mat-mdc-menu-panel {
      background-color: var(--light-blue);
      border-radius: 10px;
      min-width: 326px;
      padding: 0 !important;

      @media (max-width: 479px) {
        min-width: 290px;
      }

      @media (max-width: 359px) {
        min-width: 260px;
      }

      .mat-mdc-menu-content {
        padding: 0 !important;
      }
    }

    .notes-menu-inner {
      padding: 0;
      cursor: default;

      &:hover {
        background: transparent;
      }

      .mdc-list-item__primary-text {
        padding: 18px 20px 27px 34px;
        overflow: auto;
        width: 100%;

        @media only screen and (max-width: 1440px) {
          padding: 15px;
        }
      }

      .heading {
        font-size: $font-12;
        line-height: normal;
        font-weight: 500;
        color: $white;
        margin-bottom: 22px;
        position: relative;
        padding-left: 24px;
        margin-left: -15px;

        @media only screen and (max-width: 1440px) {
          margin-left: 0px;
        }

        &::before {
          content: "";
          position: absolute;
          width: 20px;
          height: 20px;
          background-image: url(../../../../assets/images/note-icon-white.svg);
          background-repeat: no-repeat;
          background-position: center;
          left: 0;
          top: 0;
          bottom: 0;
          margin: auto 0;
        }
      }

      .notes-list {
        margin-bottom: 16px;
        max-height: 200px;
        overflow-y: auto;
        overflow-x: hidden;

        .note-item {
          @include flex(flex-start, center, row);
          margin: 14px 0;

          &:last-child {
            .text-wrapper {
              border-bottom: 0;
            }
          }

          .text-wrapper {
            @include flex(flex-start, flex-start, column);
            padding-right: 20px;
            padding-bottom: 17px;
            border-bottom: 1px dashed #8c9cf7;
            width: 100%;

            @media only screen and (max-width: 1440px) {
              padding-bottom: 10px;
            }

            .note-text {
              font-size: $font-14;
              line-height: normal;
              font-weight: 500;
              color: $white;
              margin-bottom: 8px;
            }

            .create-date {
              font-size: $font-12;
              line-height: normal;
              font-weight: 700;
              color: #b7c1fb;
            }
          }

          .more-btn {
            max-width: 25px;
            max-height: 25px;
            min-width: auto;
            margin-right: -8px;

            .mat-icon {
              color: $white;
              margin-right: auto;
            }
          }
        }
      }

      .delete-all {
        @include flex(center, center, row);
        width: 100%;
        padding: 10px;
        background-color: #4d66f4;
        border-radius: 6px;
        font-size: $font-14;
        line-height: normal;
        font-weight: 400;
        color: $white;
      }
    }
  }

  .currency-select-panel {
    &.mat-mdc-menu-panel.mat-mdc-menu-panel {
      min-width: 360px;
      width: auto;
      border-radius: 10px;
      background: $white;

      @media (max-width: 479px) {
        min-width: 320px;
      }

      @media (max-width: 359px) {
        min-width: 100%;
      }

      .mat-mdc-menu-content {
        padding: 0 !important;
      }
    }

    .menu-wrapper {
      padding: 26px 24px;

      @media (max-width: 479px) {
        padding: 18px 18px;
      }

      @media (max-width: 359px) {
        padding-left: 12px;
        padding-right: 12px;
      }

      &:hover {
        background-color: $white !important;
      }

      span.mdc-list-item__primary-text {
        width: 100%;
      }

      .menu-heading {
        font-size: $font-18;
        line-height: normal;
        font-weight: 600;
        color: $blue-black;
        padding-bottom: 13px;
        border-bottom: 1px dotted $general-border-color;
        margin-bottom: 17px;
      }

      .search-input {
        height: 50px;
        width: 100%;
        border-radius: 7px;
        border: 1px solid var(--light-blue);
        box-shadow: 0px 6px 44px 0px rgba(65, 90, 235, 0.11);
        padding: 10px 20px;

        @media (max-width: 479px) {
          height: 40px;
          padding: 7px 15px;
        }
      }

      .currencies-list {
        margin-top: 13px;
        margin-bottom: 16px;
        padding-left: 0;
        max-height: 250px;
        overflow-y: auto;

        .list-item {
          border-radius: 10px;
          margin-bottom: 5px;

          &:last-child {
            margin-bottom: 0;
          }

          &.active {
            background-color: $selected-list-item-color;
          }

          &.mdc-list-item.mdc-list-item--with-one-line {
            padding: 10px 10px !important;
            height: 65px !important;
            max-height: 65px !important;
          }

          .mdc-list-item__primary-text {
            @include flex(space-between, center, row);
          }

          .currency-wrapper {
            @include flex(flex-start, center, row);

            .currency-flag {
              height: 29px;
              width: 29px;
              border-radius: 50px;

              img {
                width: 100%;
                height: 100%;
              }
            }

            .all-currency-flag {
              height: 38px;
              width: auto;
            }

            .currency-texts {
              @include flex(flex-start, flex-start, column);
              margin-left: 17px;
              margin-right: 14px;

              .all-currency {
                font-size: $font-16;
                line-height: normal;
                font-weight: 700;
                color: $blue-black;
              }

              .balance-label {
                font-size: $font-12;
                line-height: normal;
                font-weight: 400;
                color: $blue-black;
              }

              .balance {
                font-size: $font-22;
                line-height: normal;
                font-weight: 700;
                color: $blue-black;

                @media (max-width: 479px) {
                  font-size: $font-18;
                }

                .balance-currency {
                  font-size: $font-14;
                  line-height: 35px;
                  font-weight: 400;
                  color: $blue-black;
                }
              }
            }
          }

          .switch {
            position: relative;
            height: 30px;
            width: 50px;
            border-radius: 50px;
            background-color: rgba(120, 120, 128, 0.16);

            @media (max-width: 479px) {
              height: 22px;
              width: 40px;
              min-width: 40px;
            }

            &::before {
              content: "";
              position: absolute;
              background-color: $white;
              border-radius: 50px;
              width: 26px;
              height: 26px;
              top: 0;
              bottom: 0;
              left: 2px;
              right: auto;
              margin: auto 0;

              @media (max-width: 479px) {
                width: 18px;
                height: 18px;
              }
            }
          }

          &[aria-selected="true"] {
            .switch {
              background-color: var(--brand-primary-color);

              &::before {
                right: 2px;
                left: auto;
                background-color: $white;
              }
            }
          }
        }
      }

      .btn-wrapper {
        text-align: center;

        .switch-btn {
          height: 50px;
          padding: 14px 32px;
          font-size: $font-16;
          line-height: normal;
          font-weight: 600;
          color: $white;
          background-color: var(--light-blue);
          border-radius: 100px;

          @media (max-width: 479px) {
            height: 40px;
            padding: 8px 20px;
            min-width: 100px;
          }
        }
      }
    }
  }

  .table-view-header {
    display: flex;
    align-items: center;
    background-color: var(--content-wrapper-bg-color);
    justify-content: space-between;
    padding-bottom: 11px;

    // @media (max-width: 479px) {
    //   padding-right: 45px;
    //   position: relative;
    // }

    .date-preview {
      display: inline-flex;
      align-items: center;
      column-gap: 15px;
      border-radius: 7px;
      -webkit-border-radius: 7px;
      -moz-border-radius: 7px;
      -ms-border-radius: 7px;
      -o-border-radius: 7px;

      @media (max-width: 479px) {
        align-items: flex-start;
        flex-direction: column;
        gap: 10px;
      }

      .date-preview-box {
        display: flex;
        justify-content: space-between;
        align-items: center;
        flex-direction: row;
        background: $color-white;
        padding: 1px 11px;
        column-gap: 7px;
        border-radius: 7px;
        -webkit-border-radius: 7px;
        -moz-border-radius: 7px;
        -ms-border-radius: 7px;
        -o-border-radius: 7px;

        @media (max-width: 479px) {
          padding: 1px 8px;
        }

        .mat-date-range-input-container {
          color: #030203;
          font-family: $font-1;
          font-size: 16px;
          font-weight: 700;
          line-height: 18px;

          @media (max-width: 1440px) {
            font-size: 15px;
            line-height: 17px;
          }

          @media (max-width: 479px) {
            font-size: 14px;
            line-height: 16px;
          }
        }
      }

      .date-picker-view {
        button {
          display: flex;
          align-items: center;
          justify-content: center;

          .mat-mdc-icon-button {
            @media (max-width: 1440px) {
              width: 38px;
              height: 38px;
            }
          }

          @media (max-width: 1440px) {
            width: 38px;
            height: 38px;
            padding: 7px;
          }

          img {
            @media (max-width: 1440px) {
              width: 20px;
            }
          }
        }
      }
    }

    .date-work-week-picker {
      width: max-content;
      height: 50px;
      padding: 15px 13px;
      border-radius: 7px;
      background-color: $color-white;

      @media (max-width: 1440px) {
        height: 40px;
        padding: 7px 10px;
      }

      .mat-mdc-select-trigger {
        column-gap: 8px;

        .mat-mdc-select-value {
          .mat-mdc-select-value-text {
            color: var(--brand-primary-color);
            font-size: 14px;
            font-style: normal;
            font-weight: 700;
            line-height: 20px;
          }
        }

        .mat-mdc-select-arrow-wrapper {
          .mat-mdc-select-arrow {
            width: 10px;
            height: 24px;

            svg {
              width: 10px;
              height: 24px;
              position: relative;
              top: 0;
              left: 0;
              transform: none;
              -webkit-transform: none;
              -moz-transform: none;
              -ms-transform: none;
              -o-transform: none;
              background: url(../../../../assets/images/drop-down-arrow-calender.svg) center no-repeat;
              background-size: contain;

              path {
                display: none;
              }
            }
          }
        }
      }
    }

    .tv-header-right {
      display: flex;
      width: 100%;
      justify-content: space-between;

      @media (max-width: 479px) {
        // position: absolute;
        // right: 0;
        // bottom: 11px;
      }

      .tv-back-btn {
        display: flex;
        align-items: center;
        column-gap: 2px;
        border-radius: 7px;
        font-family: $font-1;
        font-size: 12px;
        font-weight: 400;
        line-height: 16px;
        padding: 5px 14px;
        border: none;
        outline: none;

        span {
          color: #5b5b66;
          margin-left: 5px;
          font-weight: bold;
        }
      }

      .date-view-btn {
        display: flex;
        justify-content: center;
        align-items: center;
        flex-direction: row;
        width: 50px;
        height: 50px;
        padding: 10px;
        background-color: var(--calender-plush-icon-background);
        background-image: url(../../../../assets/images/plush-sing.svg);
        background-repeat: no-repeat;
        background-position: center center;
        background-size: auto;
        border-radius: 3px;
        -webkit-border-radius: 3px;
        -moz-border-radius: 3px;
        -ms-border-radius: 3px;
        -o-border-radius: 3px;

        @media (max-width: 1440px) {
          width: 40px;
          height: 40px;
          padding: 7px;
        }
      }
    }
  }

  .date-view-option {
    min-width: max-content;
    height: 100%;
  }

  .table-view {
    padding: 18px;

    @media screen and (max-width: 480px) {
      padding: 0;
    }

    &.dashboard-table-container {
      @media (max-width: 1440px) {
        padding: 5px 10px;
      }
    }

    .mat-mdc-tab-group {
      padding: 21px 22px 22px;

      @media (max-width: 1440px) {
        padding: 15px 10px 15px;
      }
    }

    .side-tab-group {
      .mat-mdc-tab-header {
        .mat-mdc-tab-label-container {
          .mat-mdc-tab-list {
            margin-bottom: 18px;
            .mat-mdc-tab-labels {
              display: flex;
              align-items: center;
              column-gap: 106px;
              position: relative;

              @media (max-width: 1600px) {
                column-gap: 70px;
              }

              @media (max-width: 1440px) {
                column-gap: 50px;
              }

              @media (max-width: 767px) {
                column-gap: 30px;
              }

              @media (max-width: 576px) {
                column-gap: 18px;
              }

              @media (max-width: 479px) {
                flex-wrap: wrap;
                column-gap: 0;
              }

              &::before {
                content: "";
                height: 3px;
                position: absolute;
                bottom: 1.5px;
                left: 0;
                right: 0;
                border-bottom: 1px solid var(--content-wrapper-bg-color);

                @media (max-width: 479px) {
                  border-bottom: none;
                }
              }

              .mdc-tab {
                flex-grow: 0;
                padding: 0;
                border-radius: 0;
                -webkit-border-radius: 0;
                -moz-border-radius: 0;
                -ms-border-radius: 0;
                -o-border-radius: 0;

                @media (max-width: 479px) {
                  width: 50%;
                  border-bottom: 1px dashed #d9dadd;
                }

                &.mdc-tab--active {
                  .mdc-tab__content {
                    &::before {
                      background-color: $color-blue;
                    }

                    .mdc-tab__text-label {
                      color: $color-black;
                      font-weight: 700;
                    }
                  }
                }

                .mdc-tab__content {
                  padding: 12px;
                  position: relative;
                  width: 100%;

                  &::before {
                    content: "";
                    height: 3px;
                    background-color: transparent;
                    position: absolute;
                    left: 0;
                    right: 0;
                    bottom: 0px;
                    border-radius: 10px;
                    -webkit-border-radius: 10px;
                    -moz-border-radius: 10px;
                    -ms-border-radius: 10px;
                    -o-border-radius: 10px;
                  }

                  .mdc-tab__text-label {
                    color: #909199;
                    font-family: $inter;
                    font-size: 16px;
                    font-weight: 400;
                    line-height: 18px;

                    @media screen and (max-width: 480px) {
                      font-size: 12px;
                    }
                  }
                }
              }
            }
          }
        }
      }

      .mat-mdc-tab-body-wrapper {
        height: 100%;
        min-height: 444px;

        @media (max-width: 1440px) {
          min-height: 348px;
        }

        @media (max-width: 1024px) {
          min-height: 100%;
        }
      }
    }
  }
}

::ng-deep {
  .calender-view-option {
    min-width: 100px;
    max-width: 100px;
  }

  .confirm-delete {
    width: 338px;
    height: 126px;
    max-width: 100% !important;

    .mat-mdc-dialog-surface {
      &.mdc-dialog__surface {
        border-radius: 10px;
        background: #fff;
        box-shadow: 0px 14px 54px 0px rgba(0, 0, 0, 0.25);
      }

      .conform-delete-action {
        .close-btn-right {
          position: absolute;
          right: 5px;
          top: 5px;

          .close-btn {
            height: 20px;
            width: 20px;
            padding: 0px;

            .mat-mdc-button-touch-target {
              height: 20px;
              width: 20px;
            }
          }
        }

        .mat-mdc-dialog-content {
          padding: 13px 0 5px;
          color: $black;
          text-align: center;
          font-size: $font-14;
          font-style: normal;
          font-weight: 700;
          line-height: 35px;
        }

        .mat-mdc-dialog-actions {
          &.mat-mdc-dialog-actions-align-end {
            justify-content: center;

            .mat-mdc-button-base {
              @include flex(center, center, row);
              width: 128px;
              height: 40px;
              padding: 10px 16px;
              border-radius: 60px;
              font-size: $font-14;
              font-style: normal;
              font-weight: 700;
              line-height: 35px;
              letter-spacing: 0.42px;
              box-shadow: none;

              &.mat-primary {
                background: var(--light-blue);
                color: $white;
              }

              &.mat-unthemed {
                background: $disable-button-bg;
                color: var(--light-blue);
              }
            }
          }
        }
      }
    }
  }

  .mat-mdc-menu-panel {
    &.add-event-panel {
      width: 243px;
      min-width: 100%;
      max-width: 100%;
      border-radius: 10px;
      background: $white;
      box-shadow: 0px 14px 44px 0px rgba(0, 0, 0, 0.05);

      @media only screen and (max-width: 1440px) {
        width: 175px;
      }

      .mat-mdc-menu-content {
        padding: 35px 25px 16px !important;

        @media only screen and (max-width: 1440px) {
          padding: 15px 15px 7px !important;
        }

        button.mat-mdc-menu-item:hover {
          border-radius: 10px;
          background-color: #eceefe;
          padding: 0 36px;
          @media screen and (max-width: 1440px){
            padding: 0 28px;
          }
        }

        button.mat-mdc-menu-item {
          border-bottom: none;
          padding: 0 36px;
          border-bottom: 1px dashed #d9dadd;
          @media screen and (max-width: 1440px){
            padding: 0 28px;
          }

          .border-bottom-event {
            color: #d9dadd;
          }
          @media only screen and (max-width: 1440px) {
            // padding-left: 18px;
            min-height: inherit;
            min-width: inherit;
          }

          span.mdc-list-item__primary-text {
            color: $blue-black;
            font-size: 15px;
            font-style: normal;
            font-weight: 400;
            line-height: 35px;
            // border-bottom: 1px dashed $general-border-color;
            position: relative;
            padding-left: 15px;
            display: block;
            width: 100%;
            overflow: visible;
            // padding-bottom: 14px;
            // margin-bottom: 8px;

            @media only screen and (max-width: 1440px) {
              font-size: 12px;
              line-height: 24px;
              padding-left: 8px;
              padding-bottom: 5px;
              margin-bottom: 5px;
            }

            &::before {
              content: "";
              width: 23px;
              height: 23px;
              position: absolute;
              top: 50%;
              left: -22px;
              transform: translateY(-50%);
              background-image: url(../../../../assets/images/drop-down-plush.svg);
              background-repeat: no-repeat;
              border-radius: 50px;
              border: 1px solid $light-gray;
              background-position: center center;

              @media only screen and (max-width: 1440px) {
                width: 15px;
                height: 15px;
                top: 39%;
                left: -16px;
                background-size: 7px;
              }
            }
          }

          &:last-child {
            span.mdc-list-item__primary-text {
              // border-bottom: none;
              border-bottom: 1px dashed #d9dadd;
            }
          }
        }
      }
    }
  }
  .mbsc-calendar-picker-slide.mbsc-calendar-slide{
    position: absolute !important;
  }
}

