  //           this._walletService.deleteExposure(id).subscribe(res => {
  //             if (res) {
  //               this.visibleLoader = false;
  //               this.calendarData.emit();
  //             }
  //           },err => {
  //             this.visibleLoader = false;
  //           })
  //         }
  //       });
  }

  addNewAlert() {
    this.addAlertClick.emit();
  }

  addNewPayment() {
    this.addPaymentClick.emit();
  }

