@import "../../../../assets/scss/mixins";
@import "../../../../assets/scss/variable";

::ng-deep {
  .cashflow-table-wrap {
    // max-height: 441px;
    // overflow: auto;

    .cashflow-table-inner-wrap {
      @media (max-width: 1279px) {
        overflow-x: auto;
      }
    }

    .calendar-table-actions {
      .mat-mdc-menu-panel .mat-mdc-menu-content button {
        font-size: 14px;
        color: #01031C;
        border-bottom: 1px dashed #C6C8CC;
      }
    }

    .target-rate {
      border: 1px solid #BCC5FB;
      border-radius: 7px;
      padding: 5px 10px;
      font-weight: 700;
      color: #01031C;
    }

    .bold-texts {
      font-weight: 700;
      color: #01031C;
    }

    .status-text {
      border-radius: 40px;
      border: 1px solid #FF6805;
      background: #FFE9DB;
      color: #FF6805;
      padding: 5px 10px;
      display: inline-block;
      text-align: center;

      &.close,
      &.payment-completed {
        border: 1px solid rgba(69, 174, 74, 0.5);
        background: #E9F6EA;
        color: #45AE4A;
      }

      &.expired {
        border: 1px solid #F04853;
        background: #FFE0E0;
        color: #F04853;
      }

      &.future-payment,
      &.payment-ready-to-send {
        border: 1px solid #FF6805;
        background: #FFE9DB;
        color: #FF6805;
      }

      &.fix-invoice {
        border: 1px solid #FF6805;
        background: #FFE9DB;
        color: #FF6805;
        position: relative;
        display: inline-block;
        padding-right: 27px;

        &::before {
          content: "";
          position: absolute;
          height: 7px;
          width: 7px;
          border-left: 1px solid #000;
          border-bottom: 1px solid #000;
          transform: rotate(-45deg);
          top: 0;
          bottom: 3px;
          right: 13px;
          left: auto;
          margin: auto;
        }
      }
    }

    .buy-sell-arrow {
      padding-right: 30px;
    }

    .status-completed {
      border-radius: 40px;
      color: #45AE4A;
      padding: 5px 10px;
      border: 1px solid rgba(69, 174, 74, 0.50);
      background: #E9F6EA;
    }

    .margin-icon {
      margin-left: 5px;
    }

    .cashflow-data-table {
      box-shadow: none;

      // @media (max-width: 1279px) {
      //   width: 800px;
      // }

      thead {
        tr {
          height: 40px;

          th {
            color: #9291a5;
            font-size: 14px;
            font-weight: 500;
            line-height: 18px;
            text-transform: capitalize;
            border-bottom: 1px dashed #b4b6bb;
            padding: 11px 10px;

            @media (max-width: 1366px) {
              padding: 8px;
            }

            @media (max-width: 480px) {
              font-size: 12px;
            }

            // &.mat-column-createdDate {
            //   width: 15%;
            // }

            // &.mat-column-buy {
            //   width: 10%;
            // }

            // &.mat-column-sell {
            //   width: 10%;
            // }

            // &.mat-column-dueDate {
            //   width: 10%;
            // }

            &.mat-column-status {
              // width: 25%;

              // @media (max-width: 1600px) {
              //   min-width: 220px;
              // }

              // @media (max-width: 479px) {
              //   min-width: 150px;
              // }

              // &.alert-status {
              //   width: 15%;
              // }
            }

            // &.mat-column-arrow {
            //   width: 0%;
            // }

            // &.mat-column-action {
            //   width: 0%;
            // }

            // &.mat-column-targetDate {
            //   width: 16%;
            // }

            // &.mat-column-amount {
            //   width: 12%;
            // }

            // &.mat-column-type {
            //   width: 14%;

            //   @media (max-width: 1600px) {
            //     min-width: 130px;
            //   }
            // }

            // &.mat-column-rate {
            //   width: 9%;

            //   @media (max-width: 1600px) {
            //     min-width: 95px;
            //   }
            // }

            // &.mat-column-beneficiary {
            //   width: 20%;

            //   @media (max-width: 1600px) {
            //     min-width: 135px;
            //   }
            // }

            // &.mat-column-beneficiaryName {
            //   width: 20%;
            // }

            // &.mat-column-invoice {
            //   width: 10%;
            // }

            // .matColumnDef {
            //   width: 4%;
            // }
          }
        }
      }

      tbody {
        tr {
          height: 72px;

          @media (max-width: 1440px) {
            height: 65px;
          }

          &:last-child {
            td {
              border-bottom: 1px dotted #c6c8cc;
            }
          }

          td {
            border-bottom: 1px dotted #c6c8cc;
            // border-right: 1px solid #c6c8cc;
            padding: 10px 16px;

            color: #1e1b39;
            // font-family: $font-1;
            font-size: 14px;
            font-weight: 500;
            line-height: 18px;

            @media (max-width: 1440px) {
              padding: 10px;
            }

            @media (max-width: 480px) {
              font-size: 11px;
            }

            .tv_action_btn {
              display: flex;
              align-items: center;
              justify-content: center;
              flex-direction: column;
              gap: 5px;
              padding: 5px;

              .mat-icon {
                color: #4d4d4d;
              }
            }

            &:nth-last-child(2),
            &:last-child {
              border-right: none;
            }

            &.mat-column-targetDate {
              .target-date-text {
                font-size: 14px;
                font-weight: 500;
                padding: 10px 20px;
                display: inline-block;

                border-radius: 50px;
                -webkit-border-radius: 50px;
                -moz-border-radius: 50px;
                -ms-border-radius: 50px;
                -o-border-radius: 50px;
              }
            }

            &.mat-column-amount {
              font-weight: 700;
              text-align: center;
            }

            &.mat-column-createdDate {
              padding-left: 22px;

              @media (max-width: 1440px) {
                padding-left: 15px;
              }

              .created-date {
                position: relative;

                &::before {
                  content: "";
                  width: 12px;
                  height: 12px;
                  position: absolute;
                  top: 0;
                  left: -22px;
                  bottom: 0;
                  margin: auto 0;
                  background-color: #30c047;
                  border-radius: 12px;
                  -webkit-border-radius: 12px;
                  -moz-border-radius: 12px;
                  -ms-border-radius: 12px;
                  -o-border-radius: 12px;

                  @media (max-width: 1440px) {
                    width: 10px;
                    height: 10px;
                    left: -15px;
                  }
                }
              }
            }

            // &.mat-column-rate {
            //   text-align: center;
            // }

            .currency-field {
              .flag {
                height: 18px;
                width: 18px;
              }
            }
          }
        }
      }
    }
  }
}

.empty-table-message {
  margin-top: 20px;
  text-align: center;

  p {
    color: #000;
    font-size: 20px;
    font-style: normal;
    font-weight: 700;
    line-height: 50px;
  }

  .continue {
    width: 140px;
    border-radius: 50px;
    border: 1px solid var(--brand-primary-color);
    padding: 10px;
    font-size: 12px;
    font-weight: 700;
    &:disabled {
      cursor: not-allowed;
    }
  }

  .no-entry-img {
    width: 100%;
    img {
      @media screen and (min-width: 1441px) {
        width: 28%;
      }
      @media screen and (max-width: 1440px) and (min-width: 481px){
        width: 28%;
      }
      @media screen and (max-width: 480px) {
        width: 40%;
      }
    }
  }
}

::ng-deep {
  .fix-invoice-drop {
    &.mat-mdc-menu-panel {
      display: flex;
      width: 149px;
      height: 60px;
      padding: 10px 11px 10px 10px;
      justify-content: center;
      align-items: center;
      min-width: inherit;
      max-width: inherit;
      overflow: visible;
      border-radius: 5px;
      background: $white;
      box-shadow: 0px 14px 54px 0px rgba(0, 0, 0, 0.25);

      .mat-mdc-menu-content {
        padding: 0 !important;
      }

      button {
        span {
          color: #01031C;
          font-family: $manrope;
          font-size: $font-14;
          font-style: normal;
          font-weight: 600;
          line-height: 35px;
          letter-spacing: 0.42px;
        }
      }
    }
  }

  .table-pagination {
    ul {
      padding: 0;
      display: flex;
      align-items: center;
      justify-content: center;
      margin-top: 30px;
      column-gap: 10px;

      @media only screen and (max-width: 580px) {
        column-gap: 4px;
      }

      li {
        display: flex;
        justify-content: center;
        align-items: center;
        width: 32px;
        height: 32px;
        padding: 5px 4px;
        border-radius: 4px;
        border: 1px solid #DFE3E8;
        background: $white;

        a {
          color: #212B36;
          text-align: center;
          font-family: $manrope;
          font-size: $font-14;
          font-style: normal;
          font-weight: 400;
          line-height: 20px;

          &::before {
            margin-right: 0 !important;
          }

          &::after {
            margin-left: 0 !important;
          }

          &:hover {
            background: transparent;
          }
        }

        &.current{
          padding: 5px 4px;
          background: $white;
          border-color: var(--brand-primary-color);

          span {
            color: var(--brand-primary-color);
            text-align: center;
            font-family: $manrope;
            font-size: $font-14;
            font-style: normal;
            font-weight: 400;
            line-height: 20px;
            padding: 0.1875rem 0.625rem;
          }
        }
        &:hover {
          padding: 5px 4px;
          background: $white;
          border-color: var(--brand-primary-color);

          @media only screen and (max-width: 1024px) {
            border: 1px solid #DFE3E8;
          }

          a {
            color: var(--brand-primary-color);

            @media only screen and (max-width: 1024px) {
              color: #212B36;
            }
          }
        }
        &.pagination-previous {
          &.disabled {
            &::before {
              content: "\003C" !important;
              margin-right: 0 !important;
            }
          }
          a {
            &::before {
              content: "\003C" !important;
            }
          }
        }
        &.pagination-next {
          &.disabled {
            &::after {
              content: "\003E" !important;
              margin-left: 0 !important;
            }
          }
          a {
            &::after {
              content: "\003E" !important;
            }
          }
        }
      }
    }
  }
}

