.event-drawer {
  position: fixed;
  top: 0;
  right: 0;
  height: 100vh;
  z-index: 9;
  width: 618px;
  padding-top: 117px;
  border-radius: 5px;
  // background: var(--content-wrapper-bg-color);
  background: linear-gradient(to top, var(--content-wrapper-bg-color), #ffffff);
  box-shadow: 0px 4px 34px 0px rgba(0, 0, 0, 0.05);
  overflow-x: auto;

  @media only screen and (max-width: 1440px) {
    width: 418px;
    padding-top: 60px;
  }

  @media only screen and (max-width: 1440px) {
    width: 375px;
  }

  @media (max-width: 767px) {
    top: 107px;
    padding-top: 0;
    height: calc(100vh - 107px);
  }

  @media (max-width: 479px) {
    width: 100%;
  }

  &.mat-drawer-opened {
    &::before {
      content: "";
      position: fixed;
      background: rgba(0, 0, 0, 0.25);
      height: 100%;
