        height: 50px;
        min-width: 231px;

        &.dateClickedFlag {
          border-color: var(--brand-primary-color);
        }

        &.typedHedgeAmountError {
          border-color: #F04853;

          &.dateClickedFlag {
            border-color: var(--brand-primary-color);
          }
        }

        @media only screen and (max-width: 1800px) {
          min-width: 180px;
        }

        @media only screen and (max-width: 1600px) {
          min-width: 160px;
        }

        @media only screen and (max-width: 1440px) {
          height: 40px;
        }

        @media only screen and (max-width: 1280px) {
          min-width: 130px;
        }

        @media only screen and (max-width: 1024px) {
          max-width: 160px;
        }

        @media only screen and (max-width: 480px) {
          max-width: 90%;
          min-width: 90%;
          justify-content: space-between;
        }

        .input-wrapper {
          position: relative;

          .currency {
            position: relative;
            left: 0;
            top: 0;
            bottom: 0;
            margin: auto;
            display: inline-block;
            max-height: fit-content;
          }

          .amount {
            border: 0;
            height: 40px;
            padding-left: 6px;
            width: 100%;

            @media only screen and (max-width: 1440px) {
              height: 30px;

              &::-webkit-input-placeholder {
                font-size: 14px;
                line-height: 20px;
              }

              &:-ms-input-placeholder {
                font-size: 14px;
                line-height: 20px;
              }

              &::placeholder {
                font-size: 14px;
                line-height: 20px;
              }
            }
          }
        }

        .amount-select {
          @include flex(flex-start, center, row);
          border-radius: 50px;
          padding: 3px 8px;
          height: 22px;
          width: 70px;
          background-color: $success-text-color;
          color: $white;
          font-weight: 800;
          font-size: $font-12;
        }
      }
    }

    .info-icon {
      @include flex(center, center, row);
      position: absolute;
      right: -20px;
      border: 1px solid $form-label;
      top: 0;
      bottom: 0;
      margin: auto;
      border-radius: 20px;
      height: 16px;
      width: 16px;
      font-size: 11px;

      @media (min-width: 1025px) and (max-width: 1199px) {
        top: auto;
        bottom: 14px;
        margin-top: 0;
      }

      @media only screen and (max-width: 1024px) {
        position: inherit;
        right: 0;
        margin: auto auto auto 10px;
      }

      @media only screen and (max-width: 480px) {
        position: absolute;
        top: auto;
        bottom: 10%;
        right: 3%;
      }
    }
  }

  .total-cost {
    font-size: $font-12;
    font-weight: 500;
    color: $black;
    margin-bottom: 10px;
    text-align: right;
  }

  .execute-btn {
    @include flex(center, center, row);
    font-size: $font-14;
    font-weight: 500;
    color: $white;
    background-color: var(--cal-ev-blue);
    border-radius: 10px;
    width: 100%;
    height: 50px;
    padding: 10px 16px;
    margin-top: 13px;

    &:disabled {
      cursor: not-allowed;
      background-color: #F3F4F6;
      color: #000;
    }
  }

  .execute-btn-initial {
    @include flex(center, center, row);
    font-size: $font-14;
    font-weight: 500;
    color: gray;
    background-color: $disable-button-bg;
    border-radius: 10px;
    width: 100%;
    height: 50px;
    padding: 10px 16px;
    margin-top: 13px;

    &:disabled {
      cursor: not-allowed;
    }
  }

  // .execute-btn[disabled] {
  //   background-color: $disable-button-bg;
  //   color: $action-btn-color;
  //   cursor: default;
  // }
  #chart {
    max-width: 650px;
    margin: 35px auto;
  }

  .error-msg {
    color: #dc3545;
    font-size: 12px;
    line-height: 18px;
    font-weight: 700;
  }

  .confirmation-popup {
    padding: 34px 32px 16px;
    background-color: $white;
    border-radius: 10px;
    box-shadow: 0px 14px 74px 0px rgba(0, 0, 0, 0.25);
    position: absolute;
    left: 18px;
    right: 18px;
    bottom: 18px;
    width: auto;

    @media only screen and (max-width: 1440px) {
      padding: 15px;
    }

    .close-icon {
      position: absolute;
      top: 18px;
      right: 18px;
      height: 10px;
      width: 10px;

      &::before,
      &::after {
        content: "";
        position: absolute;
        width: 1px;
        height: 100%;
        background-color: $black;
        top: 0;
      }

      &::before {
        transform: rotate(45deg);
      }

      &::after {
        transform: rotate(-45deg);
      }
    }

    .message {
      font-size: $font-14;
      font-weight: 400;
      line-height: 28px;
      letter-spacing: 0.42px;
      color: $black;
      margin-bottom: 26px;
    }

    .continue[disabled] {
      background-color: $disable-button-bg;
      color: $action-btn-color;
      cursor: default;
    }

    .continue {
      @include flex(center, center, row);
      background-color: var(--cal-ev-blue);
      border-radius: 10px;
      margin-top: 23px;
      padding: 10px 16px;
      font-size: $font-14;
      font-weight: 700;
      color: $white;
      width: 100%;
    }
  }

  // ::ng-deep {
    .amount-select {
      .mat-mdc-select-value {
        color: $white;
        padding-right: 4px;
      }

      .mat-mdc-select-arrow-wrapper {
        height: 5px;
        width: 5px;
        border-left: 1px solid $white;
        border-bottom: 1px solid $white;
        transform: rotate(-45deg);

        svg {
          display: none;
        }
      }
    }

    .amount-dropdown {
      padding: 0 !important;
      background: transparent;
      box-shadow: none;
      background-color: transparent;
      min-width: 70px;

      .mat-mdc-option {
        padding: 3px 8px;
        height: 22px;
        width: 70px;
        background: $white !important;
        color: $blue-black;
        font-weight: 800;
        font-size: $font-12;
        min-height: auto;
        margin-bottom: 5px;
        border-radius: 20px;
      }
    }

    .datepicker-apply {
      width: 100%;
      border-radius: 20px;
      background-color: $disable-button-bg;
      color: var(--light-blue);
      height: 38px;
      font-weight: 700;

      &:hover {
        background-color: $black;
        color: $white;
      }
    }

    .confirm-check {
      .mdc-form-field {
        label {
          font-size: $font-14;
          font-weight: 700;
          line-height: 20px;
          letter-spacing: 0.42px;
          color: $dialog-inner-heading-color;
        }
      }
    }
  // }
}

