@import "../../../../assets/scss/variable";
@import "../../../../assets/scss/mixins";

::ng-deep {
    .cdk-overlay-pane {
        &.lock-up-popup {
            max-width: 610px !important;
            width: 100% !important;
            border-radius: 10px;
            border: 1px solid $light-gray;
            background: $color-white ;
            box-shadow: 4px 4px 20px 0px rgba(1, 3, 28, 0.04);

            @media only screen and (max-width: 1440px) {
                max-width: 560px !important;
                height: 90vh;
                overflow-y: auto;
            }

            @media only screen and (max-width: 580px) {
                max-width: 100% !important;
                width: 95vw !important;
            }
        }
    }
}

.lock-up-popup-main {
    padding: 51px 60px 110px 50px;

    @media only screen and (max-width: 1440px) {
        padding: 30px 24px;
    }

    .lock-up-popup-title {
        margin-bottom: 31px;

        @media only screen and (max-width: 1440px) {
            margin-bottom: 0;
        }

        h6 {
            font-family: $manrope;
            font-size: $font-28;
            font-style: normal;
            font-weight: 600;
            line-height: normal;
            opacity: 0.8;
            text-align: center;
            margin-bottom: 0;

            @media only screen and (max-width: 1440px) {
                font-size: 22px;
            }
        }
    }

    .lock-up-popup-close-btn {
        .approval-close-btn {
            padding: 15px 10px;

            @media only screen and (max-width: 1440px) {
                padding: 4px 10px;
            }

            button {
                width: 25px;
                height: 25px;
                display: flex;
                align-items: center;
                justify-content: center;
                padding: 0;
                margin: 0 0 0 auto;
                position: absolute;
                right: 15px;
                top: 15px;

                &:focus {
                    outline: none;
                }
            }
        }
    }

    .lock-up-popup-upper-sr {
        @include flex(start, center, row);
        column-gap: 16px;
        margin-bottom: 22px;

        @media only screen and (max-width: 480px) {
            flex-direction: column;
        }

        .lock-up-same-text {

            @media only screen and (max-width: 580px) {
                width: 100%;
            }

            .out-side-label {
                label {
                    color: $dark-gray;
                    font-family: $manrope;
                    font-size: $font-12;
                    font-style: normal;
                    font-weight: 400;
                    line-height: normal;
                }
            }

            .inside-input-text {
                position: relative;

                .lock-icon {
                    position: absolute;
                    top: 47%;
                    left: 10px;
                    right: auto;
                    bottom: auto;
                    transform: translateY(-50%);
                }

                input {
                    width: 242px;
                    height: 50px;
                    padding: 10px;
                    border-radius: 6px;
                    color: $blue-black;
                    font-family: $manrope;
                    font-size: $font-14;
                    font-style: normal;
                    font-weight: 700;
                    line-height: normal;
                    border: 1px solid #C6C8CC;

                    &.disable-input {
                        background: $white-light-gray;
                        border-color: transparent;
                        &:focus {
                            background: $white-light-gray;
                            border-color: transparent;
                        }
                    }

                    @media only screen and (max-width: 1440px) {
                        height: 40px;
                    }

                    @media only screen and (max-width: 580px) {
                        width: 100%;
                        height: 50px;
                    }

                    @media only screen and (max-width: 480px) {
                        width: 100%;
                    }

                    &:focus {
                        border: 1px solid var(--light-blue);
                        background: $color-white;
                    }

                    &.pd-left-icon {
                        padding-left: 35px;
                    }

                    &::-webkit-input-placeholder {
                        color: $blue-black;
                    }

                    &:-ms-input-placeholder {
                        color: $blue-black;
                    }

                    &::placeholder {
                        color: $blue-black;
                    }
                }

                .in-input-text {
                    position: absolute;
                    top: 50%;
                    left: auto;
                    right: 10px;
                    bottom: auto;
                    transform: translateY(-50%);

                    label {
                        @include flex(center, center, row);
                        padding: 4px 10px;
                        border-radius: 4px;
                        background: $disable-button-bg;
                        color: $profile-btn-text-color;
                        font-family: $inter;
                        font-size: $font-16;
                        font-style: normal;
                        font-weight: 300;
                        line-height: 24px;

                        @media only screen and (max-width: 1440px) {
                            font-size: 12px;
                            line-height: 22px;
                            padding: 2px 10px;
                        }
                    }
                }
            }
        }
    }

    .lock-up-popup-below-pdd {
        margin-bottom: 47px;

        @media only screen and (max-width: 1440px) {
            margin-bottom: 20px;
        }

        .lock-up-date {
            @include flex(center, center, row);
            width: 215px;
            height: 60px;
            padding: 6px 30px 6px 15px;
            gap: 10px;
            border-radius: 8px;
            background: $white-light-gray;
            margin: 0 auto;

            @media only screen and (max-width: 1440px) {
                height: 50px;
                padding: 6px 15px 6px;
            }

            @media only screen and (max-width: 580px) {
                width: 190px;
            }

            @media only screen and (max-width: 480px) {
                width: 100%;
                justify-content: flex-start;
            }

            .date-picker {

                mat-datepicker-toggle {
                    &::ng-deep {
                        .mat-mdc-icon-button {
                            padding: 0;
                            width: 28px !important;
                            height: 28px !important;
                        }
                    }
                }
            }

            .date-picker-text {

                label {
                    color: $form-label;
                    font-family: $manrope;
                    font-size: $font-12;
                    font-style: normal;
                    font-weight: 400;
                    line-height: normal;
                    display: block;
                }

                span {
                    color: $profile-btn-text-color;
                    font-family: $manrope;
                    font-size: $font-16;
                    font-style: normal;
                    font-weight: 700;
                    line-height: normal;

                    @media only screen and (max-width: 1440px) {
                        font-size: 14px;
                    }
                }
            }
        }
    }
}


.lock-up-invoice-main {

    .lock-up-invoice-title {
        color: $form-label;
        font-family: $manrope;
        font-size: $font-12;
        font-style: normal;
        font-weight: 400;
        line-height: normal;
    }

    .lock-up-invoice-img {
        position: relative;

        .strike-rate {
            position: absolute;
            left: 0;
            right: 0;
            top: 30px;
