.lock-up-warrning-msg {
    @include flex(center, center, row);
    height: 50px;
    padding: 0px 16px 0px 16px;
    gap: 8px;
    border-radius: 8px;
    background: rgba(255, 104, 5, 0.08);
    margin-top: 7px;
    margin-bottom: 24px;

    @media only screen and (max-width: 1440px) {
        height: 40px;
        margin-bottom: 18px;
    }

    p {
        color: $black;
        font-family: $manrope;
        font-size: $font-14;
        font-style: normal;
        font-weight: 500;
        line-height: normal;
        margin-bottom: 0;

        @media only screen and (max-width: 1440px) {
            font-size: 12px;
        }
    }
}

.lock-up-popup-btn-group {
    @include flex(center, center, row);
    gap: 8px;

    .lock-popup-btn-group-inner {
        @include flex(center, center, row);
        width: 141px;
        padding: 8px 32px;
        border-radius: 20px;
        font-family: $manrope;
        font-size: $font-16;
        font-style: normal;
        font-weight: 600;
        line-height: normal;

        @media only screen and (max-width: 1440px) {
            width: 100px;
            padding: 8px 15px;
            font-size: 12px;
        }

        &.popup-close-btn {
            border: 2px solid $general-border-color;
            color: var(--brand-primary-color);
        }

        &.popup-continue-btn {
            color: $white;
            background: linear-gradient(135deg, #444 0%, #000 100%);

            &.active {
                background: linear-gradient(135deg, #444 0%, #000 100%);
                color: $white;
            }

            &:hover {
                background: linear-gradient(135deg, #444 0%, #000 100%);
                color: $white;
            }

            &:disabled {
                color: $light-gray;
                background: $disable-button-bg;
            }
        }
    }
}

