    //     payment: false,
    //     transaction: true
    //   },
    //   disableClose: true,
    // }).afterClosed()
    // .subscribe((shouldReload: any) => {
    // });
  }

  ngAfterViewInit() {
    this.mySwiper = new Swiper('.swiper-container', {
      slidesPerView: 1.5,
      spaceBetween: 20
    });

    if (this.mySwiper && this.mySwiper.on) {
      this.mySwiper.on('slideChange', () => {
        this.dashboardService.setSlidePosition(this.mySwiper?.activeIndex);
      });
    }
  }

  onPayNowClick() {
  }
}

