@import '../../../../assets/scss/variable';

.lock-next-payment-container {
  display: flex;
  justify-content: space-between;
  align-items: stretch;

  .swiper-container {
    overflow: hidden;
    background-color: var(--content-wrapper-bg-color);

    .swiper-wrapper {
      position: relative;
      width: 100%;
      height: 100%;
      z-index: 1;
      display: flex;
      transition-property: transform;
      box-sizing: content-box;
    }

    .swiper-slide {
      height: initial !important;
      @media screen and (max-width:1440px) {
        min-width: 230px;
        max-width: 250px;
      }

      .pay-now-card-img {
        max-width: 150px;
      }
    }
  }
}

.lock-and-pay-card {
  border: none;
  padding: 20px 0;
  max-width: 100%;
  min-height: 450px;

  @media screen and (max-width: 1440px) {
    min-width: 185px;
    min-height: 448px;
  }

  @media screen and (min-width: 1601px) {
    min-height: 460px;
  }

  @media screen and (max-width: 767px) {
    height: 400px;
  }

  .card-body {
