      .arrow-value-up {
        background: #45AE4A;
        color: #fff;
        font-size: 15px;
        line-height: 18px;
        font-weight: 700;
        padding: 4px 10px;

        @media screen and (max-width: 1440px) {
          font-size: 12px;
          padding: 2px 15px;
        }
        @media screen and (max-width: 767px) {
          margin: 0 0 25px 0;
        }

        img {
          width: 18px;
          height: auto;
        }
      }
      .arrow-value-down {
        background: #f04853;
        color: #fff;
        font-size: 15px;
        line-height: 18px;
        font-weight: 700;
        padding: 4px 10px;

        @media screen and (max-width: 1440px) {
          font-size: 12px;
          padding: 2px 15px;
        }
        @media screen and (max-width: 767px) {
          margin: 0 0 25px 0;
        }

        img {
          width: 18px;
          height: auto;
          transform: rotate(180deg);
        }
      }
    }
    .lock-and-pay-btn {
      border: 2px solid var(--brand-primary-color);
      width: 12rem;
      border-radius: 40px;
      margin: 0 auto;
      font-style: normal;
      font-weight: 600;
      font-size: 16px;
      line-height: 22px;
      color: var(--brand-primary-color);
      padding: 10px;

      @media screen and (max-width: 1440px) {
        font-size: 14px;
        width: 10rem;
        padding: 5px;
