    .card-text {
      color: #a3a5aa;
      font-size: 14px;
      margin-bottom: 10px;
      font-family: 'Manrope';
      letter-spacing: 1px;
      @media screen and (max-width: 1440px) {
        font-size: 12px;
      }
    }
  }
}

