@import '../../../../../../assets/scss/variable';

::ng-deep {
    .adv-policy-wrapper {
        .progress-container {
            display: flex;
            align-items: center;
            gap: 8px;
            .stepper-progressbar {
              max-width: 98px;
              min-width: 98px;
              width: 100%;
              height: 12px;
              border-radius: 20px;
      
              .mdc-linear-progress__buffer {
                height: 100%;
              }
              
              .mdc-linear-progress__bar {
                height: 100%;
              }
      
                .mdc-linear-progress__bar-inner {
                  border-width: 12px;
                  border-radius: 20px;
                  border-color: #175CD3;
            
                  @media screen and (max-width: 1440px) {
                    border-width: 9px;
                  }
            
                  @media screen and (max-width: 1024px) {
                    border-width: 12px;
                  }
                }
            
              @media screen and (max-width: 1440px) {
                height: 9px;
              }
            
              @media screen and (max-width: 1024px) {
                height: 12px;
              }
            }
      
            .progress-count {
              color: #000;
              font-family: $he_heebo;
              font-size: 12px;
              font-weight: 700;
              line-height: 20px;
            }
            @media screen and (max-width:480px) {
              .stepper-progressbar{
                min-width: 60px;
                max-width: 60px;
                height: 9px;
              }
            }
        }
        .heading {
            color: #2947F2;
            font-family: $he_heebo;
            font-size: 30px;
            font-weight: 700;
            line-height: 38px;
            margin: 40px 0 34px;
        }

        .step1-wrapper {
            background-color: $white;
            padding: 50px 40px 50px 63px;
            border-radius: 10px;

            .step-form-wrap {
                text-align: center;

                .progress-container {
                    display: flex;
                    align-items: center;
                    gap: 8px;
                    justify-content: center;

                    .stepper-progressbar {
                        max-width: 98px;
                        min-width: 98px;
                        width: 100%;
                        height: 8px;
                        border-radius: 20px;

                        .mdc-linear-progress__bar-inner {
                            border-width: 8px;
                            border-radius: 50%;
                            border-color: #175CD3;
                            left: 0;
                        }
                    }

                    .progress-count {
                        color: #000;
                        font-family: $he_heebo;
                        font-size: 12px;
                        font-weight: 700;
                        line-height: 20px;
                    }

                    @media screen and (max-width:480px) {
                        .stepper-progressbar {
                            min-width: 60px;
                            max-width: 60px;
                        }
                    }
                }

                .anual-head {
                    margin: 42px 0;

                    .sub-text {
                        color: #2947F2;
                        font-family: $he_heebo;
                        font-size: 14px;
                        font-weight: 700;
                        line-height: 20px;
                        margin: 0 0 2px;
                    }

