                        }

                        .user-option {
                            background-color: transparent;
                            border: none;
                            padding: 0;
                        }
                    }
                }
            }
        }

        @media screen and (max-width:1440px) {
            .main-nav {
                padding: 0 30px 0 124px;
                .nav-inner {
                    padding: 24px 0;

                    .nav-left {
                        .wallet-list-business {
                            .currency-wrapper {
                                .wallet-ammount {
                                    font-size: 24px;
                                    margin: 0 16px 0 0;

                                    .sub-part {
                                        font-size: 18px;
                                    }
                                }
                            }
                        }
                    }
                }
            }
        }

        @media screen and (max-width:1024px) {
            .main-nav {
                padding: 0 30px 0 200px;

                .nav-inner {
                    padding: 21px 0;
                    flex-wrap: wrap;
                    margin: 0 0 0 17px;

                    .nav-option-right {
                        margin-left: auto;
                        gap: 11px;

                        .header-btn {
                            &.download-app {
                                padding: 6px 14px 6px 35px;
                                background-position: left 15px center;
                            }

                            padding: 6px 14px;
                        }

                        .add-money {
                            padding: 12px 14px;
                        }
                    }
                }
            }
        }

        @media screen and (max-width:767px) {
            .header-inner {
                display: flex;
                justify-content: space-between;
                padding: 10px 12px;

                .navbar-toggler {
                    display: block;
                    z-index: 2;
                    background-color: transparent;
                    border: none;
                    width: 20px;
                    height: 20px;
                    padding: 0;
                }
            }

            .main-nav {
                padding: 0 17px;

                .nav-inner {
                    padding:0 0 15px;
                    margin: 0;
                    .nav-left {
                        .wallet-list-business {
                            .currency-wrapper {
                                .wallet-ammount {
                                    font-size: 18px;
                                    margin: 0 14px 0 0;

                                    .sub-part {
                                        font-size: 14px;
                                    }
                                }
                            }
                        }
                    }

                    .nav-option-right {
                        .header-btn {
                            display: none;
                        }
                    }
                }
            }
        }
    }
}

