import { ChangeDetectorRef, Component, Input, Renderer2, RendererFactory2 } from '@angular/core';
import { MatSelectModule } from '@angular/material/select';

@Component({
  selector: 'app-trade-view-chart',
  imports: [MatSelectModule],
  templateUrl: './trade-view-chart.component.html',
  styleUrl: './trade-view-chart.component.scss'
})
export class TradeViewChartComponent {
  private _renderer2: Renderer2;
  tradingSource: any = {
    USDILS: "SAXO:USDILS",
  };
  @Input() tradeRate: any = 3.4324;
  @Input() firstCurrency: string = "USD";
  @Input() baseCurrency: string = "ILS";
  currencyPair: string = this.firstCurrency + this.baseCurrency;
  @Input() selectedPeriod: string = '1M';
  tradeViewChartRate: any;
  cashFlowExposureData: any;

  constructor(private cd: ChangeDetectorRef, rendererFactory: RendererFactory2) {
    this._renderer2 = rendererFactory.createRenderer(null, null);
  }

  ngOnInit(): void {
    this.getExposureDetails();
  }

  getExposureDetails() {
    this.tradingSource = this.tradingSource;
    this.loadTradingViewScripts(this.currencyPair, this.selectedPeriod);
  }


  onPeriodChange(period: string) {
    this.selectedPeriod = period;
    this.loadTradingViewScripts(this.currencyPair, period);
  }

  loadTradingViewScripts(currencyPair: string, period: string) {
    const existingScript = document.getElementById("tradingViewScript");
    if (existingScript) {
      existingScript.remove();
    }

    const script = this._renderer2.createElement("script");
    script.id = "tradingViewScript";
    script.src = "https://s3.tradingview.com/external-embedding/embed-widget-symbol-overview.js";
    script.type = "text/javascript";
    script.async = true;
    script.text = `
        {
          "symbols": [
            [
              "${this.tradingSource[currencyPair]}|${period}"
            ]
          ],
          "chartOnly": true,
          "locale": "en",
          "colorTheme": "light",
          "autosize": true,
          "showVolume": false,
          "hideMA": true,
          "hideDateRanges": true,
          "hideMarketStatus": true,
          "hideSymbolLogo": false,
          "scalePosition": "right",
          "scaleMode": "Normal",
          "fontFamily": "-apple-system, BlinkMacSystemFont, Trebuchet MS, Roboto, Ubuntu, sans-serif",
          "fontSize": "10",
          "noTimeScale": false,
          "valuesTracking": "1",
          "changeMode": "price-and-percent",
          "chartType": "area",
          "maLineColor": "#089981",
          "maLineWidth": 1,
          "maLength": 9,
          "lineWidth": 1,
          "lineType": 0,
          "lineColor": "#089981",
          "topColor": "rgba(8,153,129, 0)",
          "bottomColor": "rgba(8,153,129, 0)",
          "isTransparent": true
        }`;

    const chartContainer = document.getElementById("tradeViewChart");
    this._renderer2.appendChild(chartContainer, script);
    this.cd.detectChanges();
  }
}

import { CommonModule } from '@angular/common';
import { Component, Input } from '@angular/core';
import { NgbTooltipModule } from '@ng-bootstrap/ng-bootstrap';

@Component({
  selector: 'app-volatility-chart',
  imports: [NgbTooltipModule, CommonModule],
  templateUrl: './volatility-chart.component.html',
  styleUrl: './volatility-chart.component.scss'
})
export class VolatilityChartComponent {
  @Input() currentIndicatorValue: number = 11;
  displayIndicatorValue: number = 0;  // Will start from minVolatility and increment to currentIndicatorValue
  @Input() minVolatility: number = 7;
  @Input() maxVolatility: number = 15;

  degree: number = 0;         // The target degree based on calculation
  currentDegree: number = 0;  // This will incrementally change for smooth animation
  @Input() volatilityRank: string = 'volatile';

  ngOnInit() {
    this.calculateDegree();
    this.animateNeedle();
    this.animateIndicatorValue();
  }

  calculateDegree() {
    if (this.minVolatility && this.maxVolatility && this.currentIndicatorValue) {
      // Calculate the degree based on the formula
      this.degree = ((this.currentIndicatorValue - this.minVolatility) /
        (this.maxVolatility - this.minVolatility)) * 180;
    }
  }

  animateNeedle() {
    const step = (this.degree - this.currentDegree) / 60; // Smooth steps
    const interval = setInterval(() => {
      if (Math.abs(this.currentDegree - this.degree) < Math.abs(step)) {
        this.currentDegree = this.degree;
        clearInterval(interval);  // Stop the animation when we reach the target degree
      } else {
        this.currentDegree += step;
      }
    }, 3);  // Update every ~16ms for smooth transition
  }

  animateIndicatorValue() {
    const step = (this.currentIndicatorValue - this.displayIndicatorValue) / 60;  // Smooth steps for percentage increment
    const interval = setInterval(() => {
      if (Math.abs(this.displayIndicatorValue - this.currentIndicatorValue) < Math.abs(step)) {
        this.displayIndicatorValue = this.currentIndicatorValue;
        clearInterval(interval);  // Stop animation when we reach the target percentage
      } else {
        this.displayIndicatorValue += step;
      }
    }, 3);  // Update every 50ms for slower number increment
  }

}

