                    border: 1px solid #E4E4E5;
                    box-shadow: 0px 9px 40px 0px rgba(0, 62, 255, 0.1);
                    transition: all 0.5s ease;
                    &:hover{
                        background-color: #2947F2;
                        border-color: #2947F2;
                        color:$white;
                    }
                }

                .right-btn {
                    display: flex;
                    gap: 44px;

                    .btn-back {
                        color: #374151;
                        font-family: $he_heebo;
                        font-size: 14px;
                        font-weight: 400;
                        line-height: 45.2px;
                        padding: 0;
                    }

                    .btn-next {
                        display: flex;
                        width: 123px;
                        height: 46px;
                        padding: 10px;
                        justify-content: center;
                        align-items: center;
                        gap: 10px;
                        border-radius: 40px;
                        background: #2947F2;
                        box-shadow: 0px 9px 24px 0px rgba(0, 62, 255, 0.40);
                        color: $white;
                        font-family: $he_heebo;
                        font-size: 14px;
                        font-weight: 700;
                        line-height: 45.2px;
                        &:hover{
                            opacity: 0.9;
                        }
                        &.disabled {
                            color: #9ca3af;
                            background: #f3f4f6;
                            box-shadow: none;

                            img {
                                filter: contrast(0.5);
                            }
                        }
                    }
                }
            }
        }

        @media screen and (max-width:1440px) {
            .heading {
                font-size: 20px;
                margin: 0 0 25px;
                line-height: 30px;
            }

            .step1-wrapper {
                padding: 30px;

                .step-form-wrap {
                    .anual-head {
                        margin:30px 0;

                        .title {
                            font-size: 24px;
                            line-height: 30px;
                        }
                    }

                    .rate-wrap {
                        .inner-wrap {
                            .form-wrap {
                                .flag-wrap {
                                    padding: 0 10px 0 0;

                                    img {
                                        width: 25px;

                                        &.from-currency {
                                            margin: 0 0 0 -10px;
                                        }
                                    }
                                }
                            }
                        }
                    }
                    .rate-info {
                        margin:30px auto;
                    }
                }
            }
        }

        @media screen and (max-width:1024px) {
            .heading {
                font-size: 18px;
                margin: 0 0 20px;
                line-height: 1.2;
            }

            .step1-wrapper {
                .step-form-wrap {
                    .anual-head {
                        .title {
                            font-size: 22px;
                            line-height: 1.2;
                        }
                    }

                    .rate-wrap {
                        .inner-wrap {
                            .form-wrap {
                                height: 50px;
                            }

                            .btn-generateRate {
                                height: 50px;
                                max-width: 110px;
                            }
                        }
                    }
                }

                .btn-wrap {
                    .btn-policy {
                        height: 36px;
                        line-height: 30px;
                        padding: 5px 15px;
                    }

                    .right-btn {
                        gap: 20px;

                        .btn-back {
                            line-height: 30px;
                        }

                        .btn-next {
                            width: 90px;
                            height: 36px;
                            line-height: 30px;
                        }
                    }
                }
            }
        }

        @media screen and (max-width:767px) {
            .step1-wrapper {
                .step-form-wrap {
                    .anual-head {
                        margin: 20px 0;

                        .title {
                            font-size: 20px;
                            line-height: 1.2;
                        }
                    }

                    .rate-wrap {

                        .generate-desc {
                            font-size: 12px;
                        }

                        .error-msg-wrap {
                            font-size: 12px;
                        }
                    }

                    .rate-info {
                        p {
                            font-size: 12px;
                        }
                    }
                }
            }
        }

        @media screen and (max-width:680px) {
            .step1-wrapper {
                .step-form-wrap {
                    .anual-head {
                        .sub-text,.desc {
                            font-size: 12px;
                        }

                        .title {
                            font-size: 18px;
                            line-height: 1.2;
                        }
                    }

                    .rate-wrap {
                        .inner-wrap {
                            .form-wrap {
                                height: 50px;
                                padding: 10px;

                                .flag-wrap {
                                    img {
                                        width: 20px;
                                    }
                                }
                            }
                        }

                        .error-msg-wrap {
                            margin:5px 0 0;
                            font-size: 11px;
                        }
                    }
                }

                .btn-wrap {
                    .btn-policy {
                        font-size: 12px;
                    }

                    .right-btn {
                        gap: 10px;

                        .btn-back {
                            font-size: 12px;
                        }

                        .btn-next {
                            font-size: 12px;
                        }
                    }
                }
            }
        }

        @media screen and (max-width:420px) {
            .step1-wrapper {
                padding: 20px;

                .step-form-wrap {
                    .anual-head {
                        .title {
                            font-size: 15px;
                        }
                    }
                    .rate-wrap {
                        .inner-wrap {
                            flex-direction: column;
                            align-items: flex-end;
                            gap: 10px;
                            .btn-generateRate{
                                font-size: 12px;
                                max-width: max-content;
                                height: 40px;
                            }
                        }
                    }
                }
            }
        }
    }

}

