          .currency-pair-inner {
            display: flex;
            align-items: flex-start;
            justify-content: space-between;
            .inner-content {
              .title-pair {
                font-family: $he_heebo;
                color: #2947F2;
                font-size: 20px;
                font-weight: 500;
                line-height: 30px;
                margin-bottom: 0;

                @media screen and (max-width: 1440px) {
                  font-size: 18px;
                  line-height: 26px;
                }
              }
    
              .subtitle-pair {
                color: #7C8DA6;
                font-size: 16px;
                font-weight: 400;
                line-height: 24px;
                margin-bottom: 0;

                @media screen and (max-width: 1440px) {
                  font-size: 14px;
                  line-height: 20px;
                }
    
                @media screen and (max-width: 480px) {
                  font-size: 12px;
                }
              }
            }
          }
          .monthly-amount-container {
            display: flex;
            align-items: flex-start;
            gap: 54px;
            margin-top: 31px;

