import { Component, OnInit } from '@angular/core';
import { AbstractControl, FormControl, FormGroup, ReactiveFormsModule, ValidatorFn, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { MatIconModule } from '@angular/material/icon';
import { CommonModule } from '@angular/common';
import { TranslateModule } from '@ngx-translate/core';

@Component({
  selector: 'app-change-password',
  templateUrl: './change-password.component.html',
  styleUrls: ['./change-password.component.scss'],
  imports: [CommonModule, MatIconModule, ReactiveFormsModule, TranslateModule],
})
export class ChangePasswordComponent implements OnInit {
  hidePassword = true;
  hideConfPassword = true;
  typeVerification!: string;
  illegall!: boolean;
  hasLower!: boolean;
  minPasswordLength: number = 8;
  hasEight!: boolean;
  hasUpper!: boolean;
  hasDigit!: boolean;
  hasSpecial!: boolean;
  Validation = {
    hasLowerCase: /[a-z]/,
    hasUpperCase: /[A-Z]/,
    hasDigit: /\d/,
    hasSpecial: /[!@#$%^&*\(\)\_\-\[\]\{}\\=+|;':",.\/<>?]/,
    minPasswordLength: 8
  }
  _hasLowerCase = this.Validation.hasLowerCase;
  _hasUpperCase = this.Validation.hasUpperCase;
  _hasDigit = this.Validation.hasDigit;
  _hasSpecial = this.Validation.hasSpecial;
  isPasswordNotMatch = false;
  invalidAccount = false;
  needVerification = false;
  showLoader = false;
  error = ""
 

  resetPasswordForm = new FormGroup({
    newPassword: new FormControl('', [Validators.required, this.passwordValidator()]),
    confirmPassword: new FormControl('', [Validators.required])
  })

  constructor(private router: Router) { }


  ngOnInit() {
    this.changeFormErrors();
    this.resetPasswordForm.valueChanges.subscribe(() => {
      this.changeFormErrors();
    });
  }

  passwordValidator(): ValidatorFn {
    const passwordPattern = /^(?=.*[A-Z])(?=.*[a-z])(?=.*\d)[A-Za-z\d!@#$%^&*]*$/;

    return (control: AbstractControl): { [key: string]: any } | null => {
      const value = control.value;
      if (!value || !passwordPattern.test(value)) {
        return { 'invalidPassword': true };
      }
      return null;
    };
  }

  navigateBack() {
    this.router.navigate(['/reset-password']);
  }
  changeFormErrors() {
    this.error = "";
    this.invalidAccount = false;
  }
  changePassword() {
    if (this.resetPasswordForm?.value?.newPassword == this.resetPasswordForm?.value?.confirmPassword &&
      this.resetPasswordForm.controls.newPassword.valid && this.resetPasswordForm.controls.confirmPassword.valid
    ) {
      this.resetPasswordForm.markAllAsTouched();
      this.router.navigate(['/login']);
    }
  }

  strongPasswordCheck() {
    this.illegall = false;
    let password = this.resetPasswordForm?.value?.newPassword;
    this.hasEight = (password && password.length >= this.minPasswordLength) || false;
    this.hasLower = this._hasLowerCase?.test(password!);
    this.hasUpper = this._hasUpperCase?.test(password!);
    this.hasDigit = this._hasDigit?.test(password!);
    this.hasSpecial = this._hasSpecial?.test(password!);

    if (
      !this.hasEight ||
      !this.hasDigit ||
      !this.hasSpecial ||
      !this.hasUpper ||
      !this.hasLower
    ) {
      this.illegall = true;
    } else {
      this.illegall = false;
    }
  }
}

import { CommonModule } from '@angular/common';
import { Component, EventEmitter } from '@angular/core';
import { MatTabsModule, MatTabChangeEvent } from '@angular/material/tabs';
import { MonthlyExposureChartComponent } from "../monthly-exposure-chart/monthly-exposure-chart.component";
import { ExposureRateChartComponent } from "../exposure-rate-chart/exposure-rate-chart.component";
import { Router, RouterModule } from '@angular/router';
@Component({
  selector: 'app-charts-tabs',
  imports: [MatTabsModule, CommonModule, MonthlyExposureChartComponent, ExposureRateChartComponent,RouterModule],
  templateUrl: './charts-tabs.component.html',
  styleUrl: './charts-tabs.component.scss'
})
export class ChartsTabsComponent {
  loadMonthChart: boolean = true;
  loadExposureChart: boolean = false;
  activeIndex: number = 0;
  constructor(private router: Router){

  }
  onTabChange(event: MatTabChangeEvent) {
    const index = event.index;
    this.activeIndex = index;
    switch (index) {
      case 0:
        this.loadMonthChart = true;
        this.loadExposureChart = false;
        break;
      case 1:
        this.loadExposureChart = true;
        this.loadMonthChart = false;
        break;
      default:
        break;
    }
  }
  showHedge(){
    this.router.navigate(['/hedging']);
  }
}

import { Component, EventEmitter, Input, Output } from '@angular/core';
import { Router } from '@angular/router';
import { Observable, Subject } from 'rxjs';
import { FormsModule } from '@angular/forms';
import { CommonModule } from '@angular/common';
import { MatTooltipModule } from '@angular/material/tooltip';

@Component({
  selector: 'app-code-verification',
  templateUrl: './code-verification.component.html',
  styleUrls: ['./code-verification.component.scss'],
  imports: [CommonModule, FormsModule, MatTooltipModule],
})
export class CodeVerificationComponent {
  @Input() formData: any;
  @Input()
  typeVerification!: string;
  @Input()
  type!: string;
  @Input('timerTooltip')
  timerTooltip!: string;
  codeStatus = 'DEFAULT';
  @Output('codeValidEvent') codeValidEvent = new EventEmitter<boolean>();
  componentDestroyed = new Subject<any>();
  @Output() back: EventEmitter<boolean> = new EventEmitter();
  digit1: string = '';
  digit2: string = '';
  digit3: string = '';
  digit4: string = '';
  privacyPolicyUrl: string = 'https://okoora.com/terms-of-service/';
  termsOfUseUrl: string = 'https://okoora.com/terms-of-service/';
  code!: number;
  refreshAPIError: boolean = false;
  codeIsValid: boolean = true;
  incorrectCode = false;
  passwordResetSuccess = false;
  showLoader = false;
  codeResendSuccess = false;
  verificationError = '';
  timer: {
    minutes: string,
    seconds: string,
    active: boolean
  } = {
      minutes: '0',
      seconds: '00',
      active: false
    };
  timerIntervalId: any;

  constructor(private router: Router) {
  }
  ngOnInit() {
    this.resetFormErrors();
    // if (this.formData.valueChanges) {
    //   this.formData.valueChanges.subscribe(() => {
    //     this.resetFormErrors();
    //   });
    // }
    this.startTimer();

  }

  resetFormErrors() {
    this.verificationError = "";
  }

  performEmailVerification(action: 'SEND' | 'CHECK', email: string): void {
    let request: Observable<[string, any]>;
    this.incorrectCode = true;
    this.showLoader = false;

    if (action === 'SEND') {
      // request = combineLatest([
      //   of('SEND'),
      //   // this.KycS.SendEmailVerificationCode(email)
      // ]);
    } else if (action === 'CHECK') {

    }
    else {
      console.error(`Unhandled action ${action}`);
      return;
    }
  }

  onDigitChange(): void {
    this.verificationError = '';
  }

  allDigitsFilled(): boolean {
    return [this.digit1, this.digit2, this.digit3, this.digit4].every(d => d !== '');
  }

  verifyCode() {
    if (!this.allDigitsFilled()) {
      this.verificationError = 'Please enter all 4 digits.';
      return;
    }

    const enteredCode = `${this.digit1}${this.digit2}${this.digit3}${this.digit4}`;

    if (enteredCode === '1111') {
      this.incorrectCode = true;
      this.verificationError = 'The code you have entered is invalid. Please try again.';
      return;
    }

    this.incorrectCode = false;
    this.verificationError = '';
    this.router.navigate(['/change-password']);
  }



  onResend() {
    if (this.timer.active) {
      console.warn(`Resend code called even though the timer is active.`);
      return;
    }
    this.refreshAPIError = false;
    //this.resendCode = true;
    //this.resend.emit();
    this.handleActionByStep({ action: 'SEND' });
  }

  resendCode() {
    this.startTimer();
  }

  startTimer(): void {
    // Clear any existing timer
    if (this.timerIntervalId) {
      clearInterval(this.timerIntervalId);
    }

    const totalSeconds = 60;
    let remainingSeconds = totalSeconds;

    this.timer.active = true;

    this.timerIntervalId = setInterval(() => {
      const minutes = Math.floor(remainingSeconds / 60);
      const seconds = remainingSeconds % 60;

      this.timer.minutes = minutes.toString();
      this.timer.seconds = seconds < 10 ? '0' + seconds : seconds.toString();

      if (remainingSeconds === 0) {
        clearInterval(this.timerIntervalId);
        this.timer.active = false;
      } else {
        remainingSeconds--;
      }
    }, 1000);
  }


  resetPassword(newPassword: any, validationId: string) {
    let request: any = {
      newPassword: newPassword,
      forgetPasswordValidationId: validationId
    }

    // this._forgotPasswordService.resetPassword(request)
    //   .subscribe(
    //     (response: any) => {
    //       const statusCode = response.status;

    //       if (statusCode) {
    //         this.showLoader = false;
    //         this.passwordResetSuccess = true;
    //       }
    //     },
    //     (error: any) => {
    //       console.error(error);
    //     }
    //   );
  }
  handleEditValue(e: Event) {
    e.preventDefault();
  }
  handleSignUp(): void {
    // Error handling 
  }
  emailValue(): string {
    return this.formData.username;
  }
  restrictSpecialCharacters(event: any) {
    const invalidChars = ['<', '>', ';', '=', '+'];
    if (event?.target?.value?.length === 0 && event?.key === ".") {
      event?.preventDefault();
    } else {
      const dotOccurrences = (event?.target?.value.match(/\./g) || []).length;
      if (event?.key === "." && dotOccurrences >= 3) {
        event?.preventDefault();
      }

      if (invalidChars.includes(event.key)) {
        event.preventDefault();
      }
      const allowedKeys = /^[a-zA-Z0-9.@\u00C0-\u017F]$/;
      if (!allowedKeys.test(event.key) && !['Backspace', 'Tab', 'ArrowLeft', 'ArrowRight', 'Enter'].includes(event.key)) {
        event.preventDefault();
      }
    }
  }

  handleActionByStep(data: { action: 'SEND' | 'CHECK' }): void {
    const { action: dynamicAction } = data;
    let payload: any;
    let verificationAction: 'CHECK_EMAIL_VERIFICATION' | 'SEND_EMAIL_VERIFICATION' | 'CHECK_PHONE_VERIFICATION' | 'SEND_PHONE_VERIFICATION';

    const userEmail = this.formData.username;
    if (!userEmail) return;
    payload = { email: userEmail };
    verificationAction = dynamicAction === 'SEND' ? 'SEND_EMAIL_VERIFICATION' : 'CHECK_EMAIL_VERIFICATION';

    if (verificationAction === 'CHECK_EMAIL_VERIFICATION' || verificationAction === 'SEND_EMAIL_VERIFICATION') {
      this.performEmailVerification(dynamicAction, payload.email);
    }
  }
}

