    }


}

styleUrlsData.data:
@import "../../../assets/scss/variable";
@import "../../../assets/scss/mixins";

::ng-deep {
    .content-wrapper {
        padding: unset !important;
        background: linear-gradient(0deg, #FFF 0%, #FFF 100%), #F5F7FA;
    }
}
.app-kyc-container {
    min-height: 100vh;
}
.kyc-wrapper {
    background-repeat: no-repeat;
    background-position: center;
    background-size: cover;
    margin: 0;
    color:#111827;
    font-family: $he_heebo;

    .kyc-language-wrapper {
        position: absolute;
        top: 3rem;
        right: 5rem;
    }
    .kyc-left{
        .kyc-top{
            .logo-img{
                width: 100%;
                max-width: 124px;
                height: auto;
            }
        }
    }
}
.title {
    font-size:26px;
    font-weight: 700;
    color:#111827;
    margin: 0 0 14px;
    @media screen and (max-width: 1440px) {
        font-size: 20px;
    }
    @media screen and (max-width: 1024px) {
        font-size: 18px;    
    }
    @media screen and (max-width: 768px) {
        font-size: 16px;        
    }
}
.kyc {
    $root: &;
    @include respond(lg) {
        margin: 0 2rem;
    }

    &-wrapper {
        display: flex;
        align-items: stretch;
        @include respond(lg) {
            flex-direction: column;
        }

        & kyc-one {
            mat-form-field {
                text-align: right;
            }
        }

        kyc-phone {
            .kyc-phone {
                background-color: red;
            }
        }

        sms-verification {
            .form-group-input-wrapper {
                direction: ltr;
                justify-content: end;
            }
        }

        kyc-create-password {
            input {
                direction: ltr;
                text-align: left;
            }
        }

        kyc-personal-details {
            & .custom-input--with-icon mat-datepicker-toggle {
                left: 2rem;
                right: unset;
            }
        }

        .mat-expansion-panel {
            .inner-radio-button {
                .at-icon + div {
                    text-align: right !important;
                }
            }
        }
    }

    &-top {
        display: flex;
        align-items: center;
        justify-content: space-between;
        margin-bottom: 5rem;
    }

    &-left {
        position: relative;
        min-height: 100vh;
        flex: 60%;
        padding: 3rem;
        @include respond(lg) {
            min-height: 60vh;
            padding: 1rem;
        }

        .login-wrapper {
            display: inline-flex;
            position: absolute;
            left: 50%;
            bottom: 3%;
            transform: translateX(-50%);
            font-size: 16px;
            color:#111827;
            font-weight: 300;
            & * {
                font-size: 14px;
            }

            @include respond(lg) {
                display: none;
            }

            & .login-link {
                margin-left: 20px;
                font-weight: 500;
                color: #111827;
                text-decoration: none;

                @media screen and (max-width: 1440px) {
                    margin-left: 10px;
                }
            }
        }
    }

    &-form-section {
        width:700px;
        margin: 0 auto;
        border: 1px solid #e2e2e2;
        border-radius: 17px;
        background-color: #fff;
        // padding: 5rem 0;
        padding: 14px 10px;
        position: relative;

        @include respond(lg) {
            max-width: 95%;
            width: auto;
        }

        &.emailConfirmation,
        &.phoneConfirmation {
            #{$root}-headlines {
                a.action-anchor {
                    //color       : $color-white !important;
                    font-weight: 700;
                }
            }

            #{$root}-h--2 {
                margin-bottom: 1.2rem;
            }

            #{$root}-ah--2 {
            }

            & ::ng-deep .disclaimer {
                margin-top: 37px;
                text-align: center;
                .action-anchor{
                    margin: 0 0 0 22px;
                }
                @media screen and (max-width: 1440px) {
                    margin-top: 27px;
                    .action-anchor{
                        margin: 0 0 0 16px;
                    }
                }
                @media screen and (max-width: 1024px) {
                    margin-top: 17px;
                    .action-anchor{
                        margin: 0 0 0 10px;
                    }
                }

                & .mat-checkbox-label {
                    @include respond(lg) {
                        line-height: 1;
                    }
                }
            }
        }

        ::ng-deep .fields-wrapper {
            // max-width: 70%;
            margin: 0 auto;

            @include respond(lg) {
                max-width: 100%;
            }

            & > *:not(:last-child) {
                margin-bottom: 2rem;
            }
        }

        ::ng-deep .grouped-fields {
            display: flex;

            & > * {
                flex: 0 0 49%;

                &:not(:last-child) {
                    margin-inline-end: 2%;
                }
            }
        }
        &.createPassword,&.personalDetails {
            padding-bottom: 40px;
        }
    }

    &-headlines {
        text-align: center;

        & #{$root}-h--1,
        & #{$root}-h--2 {
            position: relative;
        }
    }
}
.loader-wrapper {
    position: relative;
    .loader {
        &.section-loader {
            position: absolute;
            top: 50%;
            left: 50%;
            transform: translate(-50%, -50%);
            width: 100%;
            height: 100%;
            justify-content: center;
            background-color: rgba(255, 255, 255, 0.7) !important;
            z-index: 999;
        }
    }
}

.kyc-p--1 {
    font-weight:300;
    line-height:normal;
    color: #111827;
    font-size:16px;
    opacity: 0.6;
    @media screen and (max-width: 1440px) {
        font-size: 14px;
    }
}
.desc-m{
    margin: 0 0 4px;
}
.desc-m-2{
    margin: 0 0 7px;
}
.m-b-step1{
    margin: 0 0 38px;
    @media screen and (max-width: 1440px) {
        margin: 0 0 28px;
    }
    @media screen and (max-width: 1024px) {
        margin: 0 0 18px;       
    }
    @media screen and (max-width: 768px) {
        margin: 0 0 8px;            
    }
}
.m-b-step4{
    margin: 0 0 35px;
    @media screen and (max-width: 1440px) {
        margin: 0 0 25px;
    }
    @media screen and (max-width: 1024px) {
        margin: 0 0 15px;       
    }
}

.kyc-form {
    // max-width: 70%;
    margin: 0 auto;
}
.kyc-form-section {
    padding-bottom:76px;
    @media screen and (max-width:1024px) {
        padding-bottom:20px;
    }
}
.confirmation-incorrect {
    position: fixed;
    z-index: 10;
    width: 100vw;
    height: 100vh;
    top: 0;
    left: 0;
    background: #fff;

    & .container {
        height: 100%;
    }

    &__wrapper {
        max-width: 40%;
        height: 100%;
        display: flex;
        flex-direction: column;
        justify-content: center;

        ::ng-deep {
            mat-icon {
                $mat-icon-size: 10rem;
                font-size: $mat-icon-size;
                width: $mat-icon-size;
                height: $mat-icon-size;
            }
        }
    }

    &__close {
        left: unset;
        // right: $fixed-items-unit;
    }
}

